INSERT INTO blocks VALUES ('index','[% IF is_moderator %]\r\nHave you <A HREF=\"[% constants.rootdir %]/metamod.pl\">Meta Moderated</A> Today?<BR>\r\n[% END %]\r\n\r\n[% UNLESS user.is_anon %]\r\n	[% group    = [\'Flock\', \'Swarm\', \'Barrel\', \'Team\', \'Squadron\',\r\n	               \'Group\', \'Cadre\'];\r\n	   modifier = [\'Rabid\', \'Uber\', \'Psycho\', \'Attack\', \'Circus\',\r\n	               \'Albino\', \'Trained\', \'Stealth\', \'Super\', \'Elite\',\r\n	               \'Random\', \'Orange\', \'Ultra\'];\r\n	   type     = [\'Mummies\', \'Elephants\', \'Midgets\', \'Ninjas\',\r\n	               \'Squirrels\', \'Chickens\', \'Geese\', \'Monkeys\', \'Geese\'];\r\n	   section.artcount = user.maxstories;\r\n	%]\r\n<FONT SIZE=\"1\"><B>This page was generated by a\r\n[% group.rand %] of [% modifier.rand %] [% type.rand %] for\r\n<A HREF=\"[% constants.rootdir %]/users.pl\">[% user.nickname %]</A>\r\n([% user.uid %]).</B></FONT><P>\r\n[% END %]\r\n\r\n[% stories %]\r\n\r\n[% UNLESS user.noboxes %]\r\n</TD>\r\n<TD>&nbsp;</TD><TD WIDTH=\"210\" ALIGN=\"CENTER\" VALIGN=\"TOP\">\r\n[% boxes %]\r\n[% END %]\r\n',NULL,10000,'template','This is the main block of html and code that gets evaled to create the main\r\nindex page.','[% IF is_moderator %]\r\nHave you <A HREF=\"[% constants.rootdir %]/metamod.pl\">Meta Moderated</A> Today?<BR>\r\n[% END %]\r\n\r\n[% UNLESS user.is_anon %]\r\n	[% group    = [\'Flock\', \'Swarm\', \'Barrel\', \'Team\', \'Squadron\',\r\n	               \'Group\', \'Cadre\'];\r\n	   modifier = [\'Rabid\', \'Uber\', \'Psycho\', \'Attack\', \'Circus\',\r\n	               \'Albino\', \'Trained\', \'Stealth\', \'Super\', \'Elite\',\r\n	               \'Random\', \'Orange\', \'Ultra\'];\r\n	   type     = [\'Mummies\', \'Elephants\', \'Midgets\', \'Ninjas\',\r\n	               \'Squirrels\', \'Chickens\', \'Geese\', \'Monkeys\', \'Geese\'];\r\n	   section.artcount = user.maxstories;\r\n	%]\r\n<FONT SIZE=\"1\"><B>This page was generated by a\r\n[% group.rand %] of [% modifier.rand %] [% type.rand %] for\r\n<A HREF=\"[% constants.rootdir %]/users.pl\">[% user.nickname %]</A>\r\n([% user.uid %]).</B></FONT><P>\r\n[% END %]\r\n\r\n[% stories %]\r\n\r\n[% UNLESS user.noboxes %]\r\n</TD>\r\n<TD>&nbsp;</TD><TD WIDTH=\"210\" ALIGN=\"CENTER\" VALIGN=\"TOP\">\r\n[% boxes %]\r\n[% END %]\r\n','',NULL,NULL,NULL,NULL,NULL,0);
