INSERT INTO blocks VALUES ('linkComment','[% subject = color? \"<FONT COLOR=\\\"$color\\\">$subject</FONT>\" : subject %]\r\n<A HREF=\"[% constants.rootdir %]/comments.pl?sid=[% sid %]&op=[% op %]&threshold=[% threshold %]&commentsort=[% commentsort %]&mode=[% mode %]&startat=[% startat %][% IF comment %]&cid=[% cid %][% ELSE %]&pid=[% pid %][% IF cid %]#[% cid %][% END %][% END %]\">[% subject %]</A>\r\n\r\n[% IF nickname %]\r\n   by [% nickname %]\r\n[% END %]\r\n\r\n[% IF not user.noscores and points %]\r\n   <FONT SIZE=\"-1\">(Score:[% points %])</FONT>\r\n[% END %]\r\n\r\n[% IF date %]\r\n    <FONT SIZE=\"-1\">[% time %]</FONT>\r\n[% END %]\r\n',NULL,10000,'template','','[% subject = color? \"<FONT COLOR=\\\"$color\\\">$subject</FONT>\" : subject %]\r\n<A HREF=\"[% constants.rootdir %]/comments.pl?sid=[% sid %]&op=[% op %]&threshold=[% threshold %]&commentsort=[% commentsort %]&mode=[% mode %]&startat=[% startat %][% IF comment %]&cid=[% cid %][% ELSE %]&pid=[% pid %][% IF cid %]#[% cid %][% END %][% END %]\">[% subject %]</A>\r\n\r\n[% IF nickname %]\r\n   by [% nickname %]\r\n[% END %]\r\n\r\n[% IF not user.noscores and points %]\r\n   <FONT SIZE=\"-1\">(Score:[% points %])</FONT>\r\n[% END %]\r\n\r\n[% IF date %]\r\n    <FONT SIZE=\"-1\">[% time %]</FONT>\r\n[% END %]\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('linkCommentPages','[% UNLESS (total < user.commentlimit) || (user.commentlimit < 1) %]\r\n	[% pages = total / user.commentlimit %]\r\n\r\n	<TR><TD BGCOLOR=\"[% constants.bg.2 %]\" ALIGN=\"CENTER\"><FONT SIZE=\"2\">\r\n	[% FOREACH p = [0 .. pages] %]\r\n		[% start = user.commentlimit * p %]\r\n		[% IF p != 0 %] | [% END %]\r\n		[% IF start == form.startat %]<B>([% END %][% Slash.linkComment({\r\n			sid	=> sid,\r\n			pid	=> pid,\r\n			cid	=> cid,\r\n			subject	=> p,\r\n			startat	=> x\r\n		}) %][% IF start == form.startat %])</B>[% END %]\r\n	[% END %]\r\n\r\n	[% IF user.breaking %]\r\n		([% constants.sitename %] Overload: CommentLimit [% user.commentlimit %])\r\n	[% END %]\r\n\r\n	</FONT></TD></TR>\r\n[% END %]\r\n',NULL,10000,'template','','[% UNLESS (total < user.commentlimit) || (user.commentlimit < 1) %]\r\n	[% pages = total / user.commentlimit %]\r\n\r\n	<TR><TD BGCOLOR=\"[% constants.bg.2 %]\" ALIGN=\"CENTER\"><FONT SIZE=\"2\">\r\n	[% FOREACH p = [0 .. pages] %]\r\n		[% start = user.commentlimit * p %]\r\n		[% IF p != 0 %] | [% END %]\r\n		[% IF start == form.startat %]<B>([% END %][% Slash.linkComment({\r\n			sid	=> sid,\r\n			pid	=> pid,\r\n			cid	=> cid,\r\n			subject	=> p,\r\n			startat	=> x\r\n		}) %][% IF start == form.startat %])</B>[% END %]\r\n	[% END %]\r\n\r\n	[% IF user.breaking %]\r\n		([% constants.sitename %] Overload: CommentLimit [% user.commentlimit %])\r\n	[% END %]\r\n\r\n	</FONT></TD></TR>\r\n[% END %]\r\n','',NULL,NULL,NULL,NULL,NULL,0);
