INSERT INTO blocks VALUES ('submit-data','[% SWITCH value %]\r\n\r\n[% CASE \'header\' %]\r\n    [% constants.sitename %] Submissions[% tbtitle %]\r\n\r\n[% CASE \'updatehead\' %]\r\n		Deleting [% subids.join(\', \') %]\r\n\r\n[% CASE \'quickieshead\' %]\r\n		Quickies Generated\r\n\r\n[% CASE \'previewhead\' %]\r\n    [% constants.sitename %] Submission Preview\r\n\r\n[% CASE \'defaulthead\' %]\r\n    [% constants.sitename %] Submissions\r\n\r\n[% CASE \'maxallowed\' %]\r\n    <P><B>Warning! you\'ve exceeded max allowed submissions for the day : [% constants.max_submissions_allowed %]</B></P>\r\n\r\n[% CASE \'error\' %]\r\n    Error\r\n\r\n[% CASE \'badsubject\' %]\r\n    Please enter a reasonable subject.\r\n\r\n[% CASE \'defaultsection\' %]\r\n    All Sections\r\n\r\n[% CASE \'defaultnote\' %]\r\n    Unclassified\r\n\r\n[% END %]\r\n',NULL,10000,'template','','[% SWITCH value %]\r\n\r\n[% CASE \'header\' %]\r\n    [% constants.sitename %] Submissions[% tbtitle %]\r\n\r\n[% CASE \'updatehead\' %]\r\n		Deleting [% subids.join(\', \') %]\r\n\r\n[% CASE \'quickieshead\' %]\r\n		Quickies Generated\r\n\r\n[% CASE \'previewhead\' %]\r\n    [% constants.sitename %] Submission Preview\r\n\r\n[% CASE \'defaulthead\' %]\r\n    [% constants.sitename %] Submissions\r\n\r\n[% CASE \'maxallowed\' %]\r\n    <P><B>Warning! you\'ve exceeded max allowed submissions for the day : [% constants.max_submissions_allowed %]</B></P>\r\n\r\n[% CASE \'error\' %]\r\n    Error\r\n\r\n[% CASE \'badsubject\' %]\r\n    Please enter a reasonable subject.\r\n\r\n[% CASE \'defaultsection\' %]\r\n    All Sections\r\n\r\n[% CASE \'defaultnote\' %]\r\n    Unclassified\r\n\r\n[% END %]\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('submit-displayForm','[% USE Slash %]\r\n<FORM ACTION=\"[% env.script_name %]\" METHOD=\"POST\">\r\n[% IF savestory %]\r\n<INPUT TYPE=\"hidden\" NAME=\"formkey\" VALUE=\"[% form.formkey %]\">\r\n[% END %]\r\n\r\n<P>Please enter your story submission here.  You need to include a subject if you\r\nwant to save it.</P>\r\n\r\n[% INCLUDE formLabel value => \"Your Name\", comment => \"Leave Blank to be Anonymous\" %]\r\n<INPUT TYPE=\"text\" NAME=\"from\" VALUE=\"[% username %]\" SIZE=\"50\">\r\n\r\n[% INCLUDE formLabel value => \"Your Email or Homepage\", comment => \"Leave Blank to be Anonymous\" %]\r\n<INPUT TYPE=\"text\" NAME=\"email\" VALUE=\"[% fakeemail %]\" SIZE=\"50\">\r\n\r\n[% INCLUDE formLabel value => \"Subject\", comment => \"Be Descriptive, Clear and Simple!\" %]\r\n<INPUT TYPE=\"text\" NAME=\"subj\" VALUE=\"[% form.subj %]\" SIZE=\"50\">\r\n\r\n<BR><FONT SIZE=\"2\">\r\n(bad subjects=\'Check This Out!\' or \'An Article\'.  \r\nWe get many submissions each day, and if yours isn\'t clear, it will\r\nbe deleted.)</FONT>\r\n\r\n[% INCLUDE formLabel value => \"Topic and Section\" %]\r\n\r\n[% tid = form.tid || \'news\' %]\r\n[% Slash.selectTopic(\'tid\', tid, 1) %]\r\n[% Slash.selectSection(\'section\', section, 0, 1) %]\r\n\r\n<BR><FONT SIZE=\"2\">(Almost everything should go under Articles)</FONT>\r\n\r\n[% IF savestory %]\r\n<P>[% INCLUDE titlebar(\"100%\", form.subj) %]\r\n<IMG SRC=\"[% constants.imagedir %]/topics/[% topic.image %]\" ALIGN=\"RIGHT\" BORDER=\"0\"\r\n	ALT=\"[% topic.alttext %]\" HSPACE=\"30\" VSPACE=\"10\"\r\n	WIDTH=\"[% topic.width %]\" HEIGHT=\"[% topic.height %]\">\r\n\r\n<P>[% username %] writes <EM>\"[% form.story %]\"</EM></P>\r\n[% END %]\r\n\r\n[% INCLUDE formLabel value => \"The Scoop\",\r\n	comment => \"HTML is fine, but double check those URLs and HTML tags!\" %]\r\n\r\n\r\n<TEXTAREA WRAP=\"VIRTUAL\" COLS=\"70\" ROWS=\"12\" NAME=\"story\">[% literalstory %]</TEXTAREA><BR>\r\n<FONT SIZE=\"2\">(Are you sure you included a URL?  Didja test them for typos?)</FONT><P>\r\n\r\n<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"PreviewStory\">\r\n[% IF savestory %]\r\n<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"SubmitStory\">\r\n[% ELSE %]\r\n(You must preview once before you can submit)\r\n[% END %]\r\n\r\n</FORM><P>\r\n',NULL,10000,'template','','[% USE Slash %]\r\n<FORM ACTION=\"[% env.script_name %]\" METHOD=\"POST\">\r\n[% IF savestory %]\r\n<INPUT TYPE=\"hidden\" NAME=\"formkey\" VALUE=\"[% form.formkey %]\">\r\n[% END %]\r\n\r\n<P>Please enter your story submission here.  You need to include a subject if you\r\nwant to save it.</P>\r\n\r\n[% INCLUDE formLabel value => \"Your Name\", comment => \"Leave Blank to be Anonymous\" %]\r\n<INPUT TYPE=\"text\" NAME=\"from\" VALUE=\"[% username %]\" SIZE=\"50\">\r\n\r\n[% INCLUDE formLabel value => \"Your Email or Homepage\", comment => \"Leave Blank to be Anonymous\" %]\r\n<INPUT TYPE=\"text\" NAME=\"email\" VALUE=\"[% fakeemail %]\" SIZE=\"50\">\r\n\r\n[% INCLUDE formLabel value => \"Subject\", comment => \"Be Descriptive, Clear and Simple!\" %]\r\n<INPUT TYPE=\"text\" NAME=\"subj\" VALUE=\"[% form.subj %]\" SIZE=\"50\">\r\n\r\n<BR><FONT SIZE=\"2\">\r\n(bad subjects=\'Check This Out!\' or \'An Article\'.  \r\nWe get many submissions each day, and if yours isn\'t clear, it will\r\nbe deleted.)</FONT>\r\n\r\n[% INCLUDE formLabel value => \"Topic and Section\" %]\r\n\r\n[% tid = form.tid || \'news\' %]\r\n[% Slash.selectTopic(\'tid\', tid, 1) %]\r\n[% Slash.selectSection(\'section\', section, 0, 1) %]\r\n\r\n<BR><FONT SIZE=\"2\">(Almost everything should go under Articles)</FONT>\r\n\r\n[% IF savestory %]\r\n<P>[% INCLUDE titlebar(\"100%\", form.subj) %]\r\n<IMG SRC=\"[% constants.imagedir %]/topics/[% topic.image %]\" ALIGN=\"RIGHT\" BORDER=\"0\"\r\n	ALT=\"[% topic.alttext %]\" HSPACE=\"30\" VSPACE=\"10\"\r\n	WIDTH=\"[% topic.width %]\" HEIGHT=\"[% topic.height %]\">\r\n\r\n<P>[% username %] writes <EM>\"[% form.story %]\"</EM></P>\r\n[% END %]\r\n\r\n[% INCLUDE formLabel value => \"The Scoop\",\r\n	comment => \"HTML is fine, but double check those URLs and HTML tags!\" %]\r\n\r\n\r\n<TEXTAREA WRAP=\"VIRTUAL\" COLS=\"70\" ROWS=\"12\" NAME=\"story\">[% literalstory %]</TEXTAREA><BR>\r\n<FONT SIZE=\"2\">(Are you sure you included a URL?  Didja test them for typos?)</FONT><P>\r\n\r\n<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"PreviewStory\">\r\n[% IF savestory %]\r\n<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"SubmitStory\">\r\n[% ELSE %]\r\n(You must preview once before you can submit)\r\n[% END %]\r\n\r\n</FORM><P>\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('submit-genQuickies','[% FOREACH sub = submissions %]\r\n    [% email = sub.2\r\n       name  = sub.3\r\n       story = sub.4\r\n    %]\r\n\r\n<P><A HREF=\"mailto:[% email %]\">[% name %]</A> writes [% story %]\r\n\r\n[% END %]\r\n',NULL,10000,'template','','[% FOREACH sub = submissions %]\r\n    [% email = sub.2\r\n       name  = sub.3\r\n       story = sub.4\r\n    %]\r\n\r\n<P><A HREF=\"mailto:[% email %]\">[% name %]</A> writes [% story %]\r\n\r\n[% END %]\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('submit-previewForm','[% USE Slash;\r\n   IF submission.email && submission.name;\r\n	subtext = \"<A HREF=\\\"$submission.email\\\">$submission.name</A> writes <EM>\\\"$submission.story\\\"</EM>\";\r\n   ELSIF submission.name;\r\n        subtext = \"submission.name writes <EM>\\\"$submission.story\\\"</EM>\";\r\n   ELSE;\r\n        subtext = submission.story;\r\n   END;\r\n%]\r\n\r\n[% IF submission.comment && user.submit_admin %]\r\n<P>Submission Notes:\r\n<TABLE WIDTH=\"95%\"><TR><TD BGCOLOR=\"[% constants.bg.2 %]\">\r\n	<FONT SIZE=\"-1\" COLOR=\"[% constants.fg.2 %]\">[% submission.comment %]</FONT>\r\n</TD></TR></TABLE>\r\n[% END %]\r\n\r\n\r\n<P>Submitted by <B>[% submission.name %]\r\n	<A HREF=\"[% submission.email %]\">[% submission.email %]</A></B>\r\n	at [% submission.time %]\r\n\r\n<P>[% subtext %]</P>\r\n\r\n[% IF user.submit_admin %]\r\n	[ <A HREF=\"[% env.script_name %]?op=Update&subid=[% subid %]\">Delete Submission</A> ]<BR>\r\n\r\n	<FORM ACTION=\"[% constants.rootdir %]/admin.pl\" METHOD=\"POST\">\r\n		<INPUT TYPE=\"hidden\" NAME=\"subid\" VALUE=\"[% subid %]\">\r\n		<BR>title <INPUT TYPE=\"text\" VALUE=\"[% submission.subj %]\" SIZE=\"50\">\r\n		<BR>[% lockTest %]\r\n    [% IF constants.use_dept %]\r\n		dept <INPUT TYPE=\"text\" SIZE=\"50\">\r\n    [% END %]\r\n    <BR>\r\n    [% Slash.selectTopic(\'tid\', submission.tid, 1) %]\r\n    [% Slash.selectSection(\'section\', section, 0, 1) %]\r\n\r\n		<INPUT TYPE=\"submit\" NAME=\"op\" VALUE=\"preview\"><BR>\r\n		<BR>Intro Copy<BR>\r\n    <TEXTAREA NAME=\"introtext\" COLS=\"70\" ROWS=\"10\" WRAP=\"VIRTUAL\"\r\n>[% Slash.stripByMode(subtext, \'literal\', 1) %]</TEXTAREA><BR>\r\n		<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"preview\"><BR>\r\n	</FORM>\r\n[% END %]\r\n',NULL,10000,'template','','[% USE Slash;\r\n   IF submission.email && submission.name;\r\n	subtext = \"<A HREF=\\\"$submission.email\\\">$submission.name</A> writes <EM>\\\"$submission.story\\\"</EM>\";\r\n   ELSIF submission.name;\r\n        subtext = \"submission.name writes <EM>\\\"$submission.story\\\"</EM>\";\r\n   ELSE;\r\n        subtext = submission.story;\r\n   END;\r\n%]\r\n\r\n[% IF submission.comment && user.submit_admin %]\r\n<P>Submission Notes:\r\n<TABLE WIDTH=\"95%\"><TR><TD BGCOLOR=\"[% constants.bg.2 %]\">\r\n	<FONT SIZE=\"-1\" COLOR=\"[% constants.fg.2 %]\">[% submission.comment %]</FONT>\r\n</TD></TR></TABLE>\r\n[% END %]\r\n\r\n\r\n<P>Submitted by <B>[% submission.name %]\r\n	<A HREF=\"[% submission.email %]\">[% submission.email %]</A></B>\r\n	at [% submission.time %]\r\n\r\n<P>[% subtext %]</P>\r\n\r\n[% IF user.submit_admin %]\r\n	[ <A HREF=\"[% env.script_name %]?op=Update&subid=[% subid %]\">Delete Submission</A> ]<BR>\r\n\r\n	<FORM ACTION=\"[% constants.rootdir %]/admin.pl\" METHOD=\"POST\">\r\n		<INPUT TYPE=\"hidden\" NAME=\"subid\" VALUE=\"[% subid %]\">\r\n		<BR>title <INPUT TYPE=\"text\" VALUE=\"[% submission.subj %]\" SIZE=\"50\">\r\n		<BR>[% lockTest %]\r\n    [% IF constants.use_dept %]\r\n		dept <INPUT TYPE=\"text\" SIZE=\"50\">\r\n    [% END %]\r\n    <BR>\r\n    [% Slash.selectTopic(\'tid\', submission.tid, 1) %]\r\n    [% Slash.selectSection(\'section\', section, 0, 1) %]\r\n\r\n		<INPUT TYPE=\"submit\" NAME=\"op\" VALUE=\"preview\"><BR>\r\n		<BR>Intro Copy<BR>\r\n    <TEXTAREA NAME=\"introtext\" COLS=\"70\" ROWS=\"10\" WRAP=\"VIRTUAL\"\r\n>[% Slash.stripByMode(subtext, \'literal\', 1) %]</TEXTAREA><BR>\r\n		<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"preview\"><BR>\r\n	</FORM>\r\n[% END %]\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('submit-saveSub','[% INCLUDE titlebar %]\r\n\r\n[% IF missingemail %]\r\n	<P>Perhaps you would like to enter an email address or a URL next time.</P>\r\n[% END %]\r\n\r\n[% IF anonsubmit %]\r\n	<P>This story has been submitted anonymously.</P>\r\n[% END %]\r\n\r\n<P><B>There are currently [% submissioncount %] submissions pending.</B></P>\r\n\r\n<P>Thanks for the submission.</P>\r\n',NULL,10000,'template','','[% INCLUDE titlebar %]\r\n\r\n[% IF missingemail %]\r\n	<P>Perhaps you would like to enter an email address or a URL next time.</P>\r\n[% END %]\r\n\r\n[% IF anonsubmit %]\r\n	<P>This story has been submitted anonymously.</P>\r\n[% END %]\r\n\r\n<P><B>There are currently [% submissioncount %] submissions pending.</B></P>\r\n\r\n<P>Thanks for the submission.</P>\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('submit-submissionEdAdmin','[% USE Slash %]\r\n\r\n<META HTTP-EQUIV=\"Refresh\" CONTENT=\"900; URL=[% env.script_name %]?op=list\">\r\n<FORM ACTION=\"[% env.script_name %]\" METHOD=\"POST\">\r\n<INPUT TYPE=\"HIDDEN\" NAME=\"note\" VALUE=\"[% form.note %]\">\r\n<INPUT TYPE=\"HIDDEN\" NAME=\"section\" VALUE=\"[% form.section %]\">\r\n\r\n<TABLE WIDTH=\"95%\" CELLPADDING=\"0\" CELLSPACING=\"0\" BORDER=\"0\">\r\n\r\n[% FOREACH sub = submissions %]\r\n	[% this_note = sub.note || \'DEFAULT\';\r\n	   select.$this_note = \' SELECTED\';\r\n	%]\r\n	<TR><TD><NOBR>\r\n		<FONT SIZE=\"1\"><INPUT TYPE=\"text\" NAME=\"comment_[% sub.subid %]\" VALUE=\"[% sub.comment %]\" SIZE=\"15\">\r\n		<SELECT NAME=\"note_[% sub.subid %]\">\r\n		[% FOREACH sel = selection %]\r\n			[% name = sel == \'DEFAULT\' ? \'\' : sel %]\r\n			<OPTION[% select.$sel %]>[% name %]</OPTION>\r\n		[% END %]\r\n	\r\n		[% Slash.selectSection(\"section_$sub.subid\", sub.section, 0, 1) %]\r\n\r\n		</FONT><INPUT TYPE=\"CHECKBOX\" NAME=\"del_[% sub.subid %]\">\r\n	</NOBR></TD><TD>\r\n	[% IF constants.submiss_ts %]\r\n		[% sub.time %]&nbsp;\r\n	[% END %]\r\n	</TD><TD>\r\n		<A HREF=\"[% env.script_name %]?op=viewsub&subid=[% sub.subid %]&note=[% form.note %][% sub.stitle%][% sub.ssection %]\"\r\n		>[% sub.strs.0 %]&nbsp;</A>\r\n	</TD><TD><FONT SIZE=\"2\">[% sub.strs.1 %]\r\n	[% UNLESS sub.is_anon %]\r\n		([% karma or 0 %])\r\n	[% END %]\r\n		<BR>[% sub.strs.2 %]</FONT></TD></TR>\r\n[% END %]\r\n\r\n</TABLE>\r\n\r\n<P>\r\n\r\n<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"Update\">\r\n[% IF form.note == \'Quik\' %]\r\n<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"GenQuickies\">\r\n[% END %]\r\n\r\n</FORM>\r\n',NULL,10000,'template','','[% USE Slash %]\r\n\r\n<META HTTP-EQUIV=\"Refresh\" CONTENT=\"900; URL=[% env.script_name %]?op=list\">\r\n<FORM ACTION=\"[% env.script_name %]\" METHOD=\"POST\">\r\n<INPUT TYPE=\"HIDDEN\" NAME=\"note\" VALUE=\"[% form.note %]\">\r\n<INPUT TYPE=\"HIDDEN\" NAME=\"section\" VALUE=\"[% form.section %]\">\r\n\r\n<TABLE WIDTH=\"95%\" CELLPADDING=\"0\" CELLSPACING=\"0\" BORDER=\"0\">\r\n\r\n[% FOREACH sub = submissions %]\r\n	[% this_note = sub.note || \'DEFAULT\';\r\n	   select.$this_note = \' SELECTED\';\r\n	%]\r\n	<TR><TD><NOBR>\r\n		<FONT SIZE=\"1\"><INPUT TYPE=\"text\" NAME=\"comment_[% sub.subid %]\" VALUE=\"[% sub.comment %]\" SIZE=\"15\">\r\n		<SELECT NAME=\"note_[% sub.subid %]\">\r\n		[% FOREACH sel = selection %]\r\n			[% name = sel == \'DEFAULT\' ? \'\' : sel %]\r\n			<OPTION[% select.$sel %]>[% name %]</OPTION>\r\n		[% END %]\r\n	\r\n		[% Slash.selectSection(\"section_$sub.subid\", sub.section, 0, 1) %]\r\n\r\n		</FONT><INPUT TYPE=\"CHECKBOX\" NAME=\"del_[% sub.subid %]\">\r\n	</NOBR></TD><TD>\r\n	[% IF constants.submiss_ts %]\r\n		[% sub.time %]&nbsp;\r\n	[% END %]\r\n	</TD><TD>\r\n		<A HREF=\"[% env.script_name %]?op=viewsub&subid=[% sub.subid %]&note=[% form.note %][% sub.stitle%][% sub.ssection %]\"\r\n		>[% sub.strs.0 %]&nbsp;</A>\r\n	</TD><TD><FONT SIZE=\"2\">[% sub.strs.1 %]\r\n	[% UNLESS sub.is_anon %]\r\n		([% karma or 0 %])\r\n	[% END %]\r\n		<BR>[% sub.strs.2 %]</FONT></TD></TR>\r\n[% END %]\r\n\r\n</TABLE>\r\n\r\n<P>\r\n\r\n<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"Update\">\r\n[% IF form.note == \'Quik\' %]\r\n<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"GenQuickies\">\r\n[% END %]\r\n\r\n</FORM>\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('submit-submissionEdTable','[% INCLUDE titlebar %]\r\n\r\n<TABLE BORDER=\"0\" CELLPADDING=\"0\" CELLSPACING=\"3\" BGCOLOR=\"[% constants.bg.2 %]\">\r\n<TR ALIGN=\"RIGHT\"><TD></TD>\r\n\r\n[% # print section headers %]\r\n[% FOREACH section = sections %]\r\n	<TD>&nbsp;<B><A HREF=\"[% env.script_name %]?section=[% section.1 %]&op=list\">[% section.0 %]</A></B>&nbsp;</TD>\r\n	[% IF section.0 == def_section %]\r\n	<TD></TD>\r\n	[% END %]\r\n[% END %]\r\n\r\n\r\n</TR>\r\n\r\n\r\n[% # print individual rows and counts %]\r\n[% FOREACH note = notes %]\r\n\r\n<TR ALIGN=\"RIGHT\">\r\n	<TD>&nbsp;<B><A HREF=\"[% env.script_name %]?note=[% note.1 %]&op=list\">[% note.0 %]</A></B>&nbsp;</TD>\r\n	[% FOREACH section = sections %]\r\n		[% s = section.0;\r\n		   n = note.0;\r\n		   count = sn.$s.$n || 0;\r\n		   bgcolor = \" BGCOLOR=\\\"$constants.bg.1\\\"\" IF n == cur_note && s == cur_section;\r\n		%]\r\n	<TD[% bgcolor %]><A HREF=\"[% env.script_name %]?section=[% section.1 %]&op=list&note=[% note.1 %]\">[% count %]</A>&nbsp;</TD>\r\n		[% IF section.0 == def_section %]\r\n	<TD></TD>\r\n		[% END %]\r\n	[% END %]\r\n</TR>\r\n\r\n[% END %]\r\n\r\n</TABLE>\r\n',NULL,10000,'template','','[% INCLUDE titlebar %]\r\n\r\n<TABLE BORDER=\"0\" CELLPADDING=\"0\" CELLSPACING=\"3\" BGCOLOR=\"[% constants.bg.2 %]\">\r\n<TR ALIGN=\"RIGHT\"><TD></TD>\r\n\r\n[% # print section headers %]\r\n[% FOREACH section = sections %]\r\n	<TD>&nbsp;<B><A HREF=\"[% env.script_name %]?section=[% section.1 %]&op=list\">[% section.0 %]</A></B>&nbsp;</TD>\r\n	[% IF section.0 == def_section %]\r\n	<TD></TD>\r\n	[% END %]\r\n[% END %]\r\n\r\n\r\n</TR>\r\n\r\n\r\n[% # print individual rows and counts %]\r\n[% FOREACH note = notes %]\r\n\r\n<TR ALIGN=\"RIGHT\">\r\n	<TD>&nbsp;<B><A HREF=\"[% env.script_name %]?note=[% note.1 %]&op=list\">[% note.0 %]</A></B>&nbsp;</TD>\r\n	[% FOREACH section = sections %]\r\n		[% s = section.0;\r\n		   n = note.0;\r\n		   count = sn.$s.$n || 0;\r\n		   bgcolor = \" BGCOLOR=\\\"$constants.bg.1\\\"\" IF n == cur_note && s == cur_section;\r\n		%]\r\n	<TD[% bgcolor %]><A HREF=\"[% env.script_name %]?section=[% section.1 %]&op=list&note=[% note.1 %]\">[% count %]</A>&nbsp;</TD>\r\n		[% IF section.0 == def_section %]\r\n	<TD></TD>\r\n		[% END %]\r\n	[% END %]\r\n</TR>\r\n\r\n[% END %]\r\n\r\n</TABLE>\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('submit-submissionEdUser','<TABLE WIDTH=\"95%\" CELLPADDING=\"0\" CELLSPACING=\"0\" BORDER=\"0\">\r\n\r\n[% FOREACH sub = submissions %]\r\n	<TR><TD>[% sub.note %]</TD>\r\n\r\n	<TD>[% sub.section %]</TD><TD>\r\n	[% IF constants.submiss_ts %]\r\n		[% sub.time %]&nbsp;\r\n	[% END %]\r\n	</TD><TD>\r\n		<A HREF=\"[% env.script_name %]?op=viewsub&subid=[% sub.subid %]&note=[% form.note %][% sub.stitle %]\"\r\n		>[% sub.strs.0 %]&nbsp;</A>\r\n	</TD><TD><FONT SIZE=\"-1\">[% sub.strs.1 %]<BR>[% sub.strs.2 %]</FONT></TD></TR>\r\n	<TR><TD COLSPAN=\"6\"><IMG SRC=\"[% constants.imagedir %]/pix.gif\" ALT=\"\" HEIGHT=\"3\"></TD></TR>\r\n[% END %]\r\n\r\n</TABLE>\r\n\r\n<P>\r\n',NULL,10000,'template','','<TABLE WIDTH=\"95%\" CELLPADDING=\"0\" CELLSPACING=\"0\" BORDER=\"0\">\r\n\r\n[% FOREACH sub = submissions %]\r\n	<TR><TD>[% sub.note %]</TD>\r\n\r\n	<TD>[% sub.section %]</TD><TD>\r\n	[% IF constants.submiss_ts %]\r\n		[% sub.time %]&nbsp;\r\n	[% END %]\r\n	</TD><TD>\r\n		<A HREF=\"[% env.script_name %]?op=viewsub&subid=[% sub.subid %]&note=[% form.note %][% sub.stitle %]\"\r\n		>[% sub.strs.0 %]&nbsp;</A>\r\n	</TD><TD><FONT SIZE=\"-1\">[% sub.strs.1 %]<BR>[% sub.strs.2 %]</FONT></TD></TR>\r\n	<TR><TD COLSPAN=\"6\"><IMG SRC=\"[% constants.imagedir %]/pix.gif\" ALT=\"\" HEIGHT=\"3\"></TD></TR>\r\n[% END %]\r\n\r\n</TABLE>\r\n\r\n<P>\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('submit-yourPendingSubmissions','[% INCLUDE titlebar %]\r\n\r\n<P>Here are your recent submissions to [% constants.sitename %],\r\nand their staus within the system:</P>\r\n\r\n<UL>\r\n[% FOREACH submission = submissions %]\r\n    [% time    = submission.0\r\n       subj    = submission.1\r\n       section = submission.2\r\n       tid     = submission.3\r\n       del     = submission.4\r\n     %]\r\n\r\n    <LI>[% time %] [% subj %] ([% section %],[% tid %])\r\n    [% # this is a multiple ?: operator thingy ... i hope it makes sense ...\r\n       # if del == 1 it is rejected, if del == 2 it is accepted, else it is pending %]\r\n    [% del == 1 ? \'(rejected)\' : del == 2 ? \'(accepted)\' : \'\' %]\r\n    </LI>\r\n[% END %]\r\n</UL>\r\n',NULL,10000,'template','','[% INCLUDE titlebar %]\r\n\r\n<P>Here are your recent submissions to [% constants.sitename %],\r\nand their staus within the system:</P>\r\n\r\n<UL>\r\n[% FOREACH submission = submissions %]\r\n    [% time    = submission.0\r\n       subj    = submission.1\r\n       section = submission.2\r\n       tid     = submission.3\r\n       del     = submission.4\r\n     %]\r\n\r\n    <LI>[% time %] [% subj %] ([% section %],[% tid %])\r\n    [% # this is a multiple ?: operator thingy ... i hope it makes sense ...\r\n       # if del == 1 it is rejected, if del == 2 it is accepted, else it is pending %]\r\n    [% del == 1 ? \'(rejected)\' : del == 2 ? \'(accepted)\' : \'\' %]\r\n    </LI>\r\n[% END %]\r\n</UL>\r\n','',NULL,NULL,NULL,NULL,NULL,0);
