INSERT INTO blocks VALUES ('users-previewSlashbox','<!-- note that parameters passed to this template are passed to titlebar -->\r\n[% INCLUDE titlebar title=title %]\r\n\r\nWhat you see on the right hand side is a preview of the block thingee\r\nlabeled \"[% cleantitle %]\".  If you select it from the\r\n<A HREF=\"[% constants.rootdir %]/users.pl?op=preferences\">Preferences Page</A>,\r\nyou will have that little block added to the right hand side of your\r\n<A HREF=\"[% constants.rootdir %]/index.pl\">Custom [% constants.sitename %]</A> page.\r\nExciting?  Not really, but its a great way to waste time.\r\n\r\n[% IF is_editable %]\r\n	<P>Edit <A HREF=\"[% constants.rootdir %]/admin.pl?op=blocked&bid=[% form.bid %]\">[% form.bid %]</A>\r\n[% END %]\r\n\r\n	</TD><TD WIDTH=\"180\" VALIGN=\"TOP\">\r\n',NULL,10000,'template','','<!-- note that parameters passed to this template are passed to titlebar -->\r\n[% INCLUDE titlebar title=title %]\r\n\r\nWhat you see on the right hand side is a preview of the block thingee\r\nlabeled \"[% cleantitle %]\".  If you select it from the\r\n<A HREF=\"[% constants.rootdir %]/users.pl?op=preferences\">Preferences Page</A>,\r\nyou will have that little block added to the right hand side of your\r\n<A HREF=\"[% constants.rootdir %]/index.pl\">Custom [% constants.sitename %]</A> page.\r\nExciting?  Not really, but its a great way to waste time.\r\n\r\n[% IF is_editable %]\r\n	<P>Edit <A HREF=\"[% constants.rootdir %]/admin.pl?op=blocked&bid=[% form.bid %]\">[% form.bid %]</A>\r\n[% END %]\r\n\r\n	</TD><TD WIDTH=\"180\" VALIGN=\"TOP\">\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('users-tildeEd','[% INCLUDE titlebar title=title width=\"100%\" %]\r\n\r\n<TABLE WIDTH=\"95%\" BORDER=\"0\" CELLPADDING=\"3\" CELLSPACING=\"3\" ALIGN=\"CENTER\">\r\n	<TR BGCOLOR=\"[% constants.bg.3 %]\">\r\n		<TH><FONT COLOR=\"[% constants.fg.3 %]\" SIZE=\"[% constants.fontbase %]\">Authors</FONT></TH>\r\n		<TH><FONT COLOR=\"[% constants.fg.3 %]\" SIZE=\"[% constants.fontbase %]\">Topics</FONT></TH>\r\n		<TH><FONT COLOR=\"[% constants.fg.3 %]\" SIZE=\"[% constants.fontbase %]\">Sections</FONT></TH>\r\n	</TR>\r\n	<TR BGCOLOR=\"[% constants.bg.2 %]\">\r\n		<TD VALIGN=\"TOP\">\r\n\r\n[% FOREACH aid = aidref.keys %]\r\n		<INPUT TYPE=\"CHECKBOX\" NAME=\"exaid_[% aid %]\"[% aidref.$aid.checked %]>[% aid %]<BR>\r\n[% END %]\r\n\r\n		</TD>\r\n		<TD VALIGN=\"TOP\"><MULTICOL COLS=\"3\">\r\n\r\n[% FOREACH tid = tidref.keys %]\r\n		<INPUT TYPE=\"CHECKBOX\" NAME=\"extid_[% tid %]\"[% tidref.tid %]>[% tidref.$tid.alttext %]<BR>\r\n		\r\n[% END %]\r\n\r\n		</MULTICOL></TD>\r\n		<TD VALIGN=\"TOP\"><MULTICOL COLS=\"3\">\r\n[% FOREACH section = sectionref.keys %]\r\n		<INPUT TYPE=\"CHECKBOX\" NAME=\"exsect_[% section %]\"[% sectionref.$section.checked %]>[% sectionref.$section.title %]<BR>\r\n[% END %]\r\n		</MULTICOL>\r\n		</TD>\r\n	</TR>\r\n</TABLE>\r\n[% INCLUDE titlebar title=customize_title %]\r\n<TABLE WIDTH=\"95%\" ALIGN=\"CENTER\" BORDER=\"0\">\r\n	<TR>\r\n		<TD> \r\n		<P>Look ma, I\'m configurable!\r\n        	<B>Important:</B> If you leave these all unchecked, it means you\r\n        	want the <I>default</I> selection of boxes.  If you start selecting\r\n        	boxes, remember to set <B>all</B> of them that you want because the\r\n        	default selection will be <B>ignored</B>.  Default entries are bolded.\r\n\r\n        	<P><B>User Space</B> (check \'user space\' below and whatever\r\n        	you place here will appear your custom [% constants.sitename %])<BR>\r\n                <TEXTAREA NAME=\"mylinks\" rows=5 COLS=\"60\" WRAP=\"VIRTUAL\">[% userspace %]</TEXTAREA>\r\n		<P>\r\n		<MULTICOL COLS=\"3\">\r\n		\r\n[% FOREACH bid = section_descref.keys %]\r\n	[% IF section_descref.$bid.boldflag %]\r\n		<B>\r\n	[% END %]\r\n\r\n		<INPUT TYPE=\"CHECKBOX\" NAME=\"exboxes_[% bid %]\"[% section_descref.$bid.checked %]>\r\n		<A HREF=\"[% env.script_name %]?op=preview&bid=[% bid %]\">\r\n\r\n	[% IF section_descref.$bid.srandflag %]\r\n		Semi-Random Box\r\n	[% ELSE %]\r\n		[% bid %]\r\n	[% END %]\r\n\r\n	 	</A><BR>\r\n\r\n	[% IF section_descref.$bid.boldflag %]\r\n          	</B>\r\n	[% END %]\r\n\r\n[% END %]\r\n\r\n		</MULTICOL>\r\n		</TD>\r\n	</TR>\r\n</TABLE><P>\r\n\r\n[% tilded_box_msg %]',NULL,500,'template','','[% INCLUDE titlebar title=title width=\"100%\" %]\r\n\r\n<TABLE WIDTH=\"95%\" BORDER=\"0\" CELLPADDING=\"3\" CELLSPACING=\"3\" ALIGN=\"CENTER\">\r\n	<TR BGCOLOR=\"[% constants.bg.3 %]\">\r\n		<TH><FONT COLOR=\"[% constants.fg.3 %]\" SIZE=\"[% constants.fontbase %]\">Authors</FONT></TH>\r\n		<TH><FONT COLOR=\"[% constants.fg.3 %]\" SIZE=\"[% constants.fontbase %]\">Topics</FONT></TH>\r\n		<TH><FONT COLOR=\"[% constants.fg.3 %]\" SIZE=\"[% constants.fontbase %]\">Sections</FONT></TH>\r\n	</TR>\r\n	<TR BGCOLOR=\"[% constants.bg.2 %]\">\r\n		<TD VALIGN=\"TOP\">\r\n\r\n[% FOREACH aid = aidref.keys %]\r\n		<INPUT TYPE=\"CHECKBOX\" NAME=\"exaid_[% aid %]\"[% aidref.$aid.checked %]>[% aid %]<BR>\r\n[% END %]\r\n\r\n		</TD>\r\n		<TD VALIGN=\"TOP\"><MULTICOL COLS=\"3\">\r\n\r\n[% FOREACH tid = tidref.keys %]\r\n		<INPUT TYPE=\"CHECKBOX\" NAME=\"extid_[% tid %]\"[% tidref.tid %]>[% tidref.$tid.alttext %]<BR>\r\n		\r\n[% END %]\r\n\r\n		</MULTICOL></TD>\r\n		<TD VALIGN=\"TOP\"><MULTICOL COLS=\"3\">\r\n[% FOREACH section = sectionref.keys %]\r\n		<INPUT TYPE=\"CHECKBOX\" NAME=\"exsect_[% section %]\"[% sectionref.$section.checked %]>[% sectionref.$section.title %]<BR>\r\n[% END %]\r\n		</MULTICOL>\r\n		</TD>\r\n	</TR>\r\n</TABLE>\r\n[% INCLUDE titlebar title=customize_title %]\r\n<TABLE WIDTH=\"95%\" ALIGN=\"CENTER\" BORDER=\"0\">\r\n	<TR>\r\n		<TD> \r\n		<P>Look ma, I\'m configurable!\r\n        	<B>Important:</B> If you leave these all unchecked, it means you\r\n        	want the <I>default</I> selection of boxes.  If you start selecting\r\n        	boxes, remember to set <B>all</B> of them that you want because the\r\n        	default selection will be <B>ignored</B>.  Default entries are bolded.\r\n\r\n        	<P><B>User Space</B> (check \'user space\' below and whatever\r\n        	you place here will appear your custom [% constants.sitename %])<BR>\r\n                <TEXTAREA NAME=\"mylinks\" rows=5 COLS=\"60\" WRAP=\"VIRTUAL\">[% userspace %]</TEXTAREA>\r\n		<P>\r\n		<MULTICOL COLS=\"3\">\r\n		\r\n[% FOREACH bid = section_descref.keys %]\r\n	[% IF section_descref.$bid.boldflag %]\r\n		<B>\r\n	[% END %]\r\n\r\n		<INPUT TYPE=\"CHECKBOX\" NAME=\"exboxes_[% bid %]\"[% section_descref.$bid.checked %]>\r\n		<A HREF=\"[% env.script_name %]?op=preview&bid=[% bid %]\">\r\n\r\n	[% IF section_descref.$bid.srandflag %]\r\n		Semi-Random Box\r\n	[% ELSE %]\r\n		[% bid %]\r\n	[% END %]\r\n\r\n	 	</A><BR>\r\n\r\n	[% IF section_descref.$bid.boldflag %]\r\n          	</B>\r\n	[% END %]\r\n\r\n[% END %]\r\n\r\n		</MULTICOL>\r\n		</TD>\r\n	</TR>\r\n</TABLE><P>\r\n\r\n[% tilded_box_msg %]','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('users-titles','[% SWITCH value %]\r\n\r\n[% CASE \'previewslashbox_title\' %]\r\n	Preview [% cleantitle %]\r\n\r\n[% CASE \'newUser_title\' %]\r\n    User [% form.newuser %] created.\r\n\r\n[% CASE \'mailPassword_email_title\' %]\r\n    [% constants.sitename %] user password for [% nickname %]\r\n\r\n[% CASE \'userInfo_main_title\' %]\r\n    Welcome back [% nick %] ([% uid %])\r\n\r\n[% CASE \'userInfo_user_title\' %]\r\n   User Info for [% nick %] ([% uid %])\r\n\r\n[% CASE \'editUser_title\' %]\r\n	Editing [% user_edit.nickname %] ( [% user_edit.uid %]) [% user_edit.realemail %]\r\n\r\n[% CASE \'tildeEd_title\' %]\r\n	Exclude Stories from the Homepage\r\n\r\n[% CASE \'tildeEd_customize_title\' %]\r\n	Customize Slashboxes\r\n\r\n[% CASE \'editHome_title\' %]\r\n   	Customize [% constants.sitename %]\'s Display\r\n\r\n[% CASE \'editComm_title\' %]\r\n	Comment Options\r\n\r\n[% CASE \'displayForm_new_title\' %]\r\n	I\'m a New User!\r\n\r\n[% CASE \'displayForm_dup_title\' %]\r\n	Duplicate Account!\r\n\r\n[% CASE \'displayForm_title\' %]\r\n	Login\r\n\r\n[% CASE \'displayForm_err_title\' %]\r\n	Error Logging In\r\n\r\n[% CASE \'saveUser_email_title\' %]\r\n	[% constants.sitename %] user email change for [% nickname %]\r\n\r\n[% END %]\r\n',NULL,10000,'template','A template with logic to return the varioustitle in users.pl, used\r\nby the subroutine getTitles in users.pl','[% SWITCH value %]\r\n\r\n[% CASE \'previewslashbox_title\' %]\r\n	Preview [% cleantitle %]\r\n\r\n[% CASE \'newUser_title\' %]\r\n    User [% form.newuser %] created.\r\n\r\n[% CASE \'mailPassword_email_title\' %]\r\n    [% constants.sitename %] user password for [% nickname %]\r\n\r\n[% CASE \'userInfo_main_title\' %]\r\n    Welcome back [% nick %] ([% uid %])\r\n\r\n[% CASE \'userInfo_user_title\' %]\r\n   User Info for [% nick %] ([% uid %])\r\n\r\n[% CASE \'editUser_title\' %]\r\n	Editing [% user_edit.nickname %] ( [% user_edit.uid %]) [% user_edit.realemail %]\r\n\r\