INSERT INTO blocks VALUES ('users-userInfo','[% INCLUDE titlebar title=title width=\"95%\" %]\r\n\r\n<A HREF=\"[% homepage %]\">[% homepage %]</A><BR>\r\n<A HREF=\"mailto:[% fakeemail %]\">[% fakeemail %]</A><BR>\r\n\r\n\r\n[% IF nickmatch_flag %]\r\n\r\n	<P>This is <B>your</B> User Info page.  There are thousands more, but this one is yours.\r\n	You most likely are not so interested in you, and probably would be most interested in\r\n	clicking the \"Edit User Info\" and \"Customize...\" links you see up top there so you can\r\n	customize [% constants.sitename %], change your password, or just click pretty widgets to kill time.\r\n\r\n	[% IF mod_flag %]\r\n	<P>You\'re a moderator with [% points %] points. Please read the\r\n	<A HREF=\"[% constants.rootdir %]/moderation.shtml\">Moderator Guidelines</A> \r\n	before you do any moderation.<BR><P>\r\n	[%END %]\r\n\r\n	<CENTER><IMG SRC=\"[% constants.imagedir %]/greendot.gif\" WIDTH=\"75%\" HEIGHT=\"1\" ALIGN=\"CENTER\"><BR></CENTER>\r\n[% END %]\r\n\r\n\r\n\r\n[% IF karma_flag %]\r\n	<B>Karma</B> [% karma %] (mostly the sum of moderation done to users comments)<BR>\r\n[% END %]\r\n\r\n[% IF bio %]\r\n	<B>User Bio</B><BR>[% bio %]<P>\r\n[% END %]\r\n\r\n[% IF public_key %]\r\n	<B>Public Key</B><BR><PRE>\r\n	[% public_key %]\r\n	</PRE><P>\r\n[% END %]\r\n\r\n<B>[% nick %] has posted [% rows %] comments</B> (this only counts the last few weeks)<BR><P>\r\n\r\n[% FOREACH n = commentstruct %]\r\n	[% IF commentstruct.n.replies %] \r\n	Replies: [% commentstruct.n.replies %]\r\n	[% END %]\r\n	\r\n	[% IF commentstruct.n.story %]\r\n	<BR><B>[% n + 1 %]</B> <A HREF=\"[% constants.rootdir %]/comments.pl?sid=[% commentstruct.n.story.sid %]&cid=$cid\">\r\n	[% commentstruct.n.story.subj %]</A> posted on [% commentstruct.n.story.cdate %] \r\n	(Score:[% commentstruct.n.story.pts %] [% commentstruct.n.story.replies %])\r\n	<FONT SIZE=\"2\">\r\n	\r\n	[% ELSE %]\r\n	<BR>attached to <A HREF=\"[% constants.rootdir %]/pollBooth.pl?qid=[% commentstruct.n.story.sid %]\">[% commentstruct.n.story.question %]</A></font>\r\n	[% END %]\r\n[% END %]\r\n',NULL,500,'template','','[% INCLUDE titlebar title=title width=\"95%\" %]\r\n\r\n<A HREF=\"[% homepage %]\">[% homepage %]</A><BR>\r\n<A HREF=\"mailto:[% fakeemail %]\">[% fakeemail %]</A><BR>\r\n\r\n\r\n[% IF nickmatch_flag %]\r\n\r\n	<P>This is <B>your</B> User Info page.  There are thousands more, but this one is yours.\r\n	You most likely are not so interested in you, and probably would be most interested in\r\n	clicking the \"Edit User Info\" and \"Customize...\" links you see up top there so you can\r\n	customize [% constants.sitename %], change your password, or just click pretty widgets to kill time.\r\n\r\n	[% IF mod_flag %]\r\n	<P>You\'re a moderator with [% points %] points. Please read the\r\n	<A HREF=\"[% constants.rootdir %]/moderation.shtml\">Moderator Guidelines</A> \r\n	before you do any moderation.<BR><P>\r\n	[%END %]\r\n\r\n	<CENTER><IMG SRC=\"[% constants.imagedir %]/greendot.gif\" WIDTH=\"75%\" HEIGHT=\"1\" ALIGN=\"CENTER\"><BR></CENTER>\r\n[% END %]\r\n\r\n\r\n\r\n[% IF karma_flag %]\r\n	<B>Karma</B> [% karma %] (mostly the sum of moderation done to users comments)<BR>\r\n[% END %]\r\n\r\n[% IF bio %]\r\n	<B>User Bio</B><BR>[% bio %]<P>\r\n[% END %]\r\n\r\n[% IF public_key %]\r\n	<B>Public Key</B><BR><PRE>\r\n	[% public_key %]\r\n	</PRE><P>\r\n[% END %]\r\n\r\n<B>[% nick %] has posted [% rows %] comments</B> (this only counts the last few weeks)<BR><P>\r\n\r\n[% FOREACH n = commentstruct %]\r\n	[% IF commentstruct.n.replies %] \r\n	Replies: [% commentstruct.n.replies %]\r\n	[% END %]\r\n	\r\n	[% IF commentstruct.n.story %]\r\n	<BR><B>[% n + 1 %]</B> <A HREF=\"[% constants.rootdir %]/comments.pl?sid=[% commentstruct.n.story.sid %]&cid=$cid\">\r\n	[% commentstruct.n.story.subj %]</A> posted on [% commentstruct.n.story.cdate %] \r\n	(Score:[% commentstruct.n.story.pts %] [% commentstruct.n.story.replies %])\r\n	<FONT SIZE=\"2\">\r\n	\r\n	[% ELSE %]\r\n	<BR>attached to <A HREF=\"[% constants.rootdir %]/pollBooth.pl?qid=[% commentstruct.n.story.sid %]\">[% commentstruct.n.story.question %]</A></font>\r\n	[% END %]\r\n[% END %]\r\n','',NULL,NULL,NULL,NULL,NULL,0);
