INSERT INTO blocks VALUES ('article-display','[% USE Slash;\r\n\r\n# Start of article_display\r\ndata = Slash.displayStory(story.sid, \'Full\', calling_script);\r\n# data -> (storydisplay, storydata, author, topic)\r\ndata.0;\r\n%]\r\n[% IF form.refresh; fontsize = constants.fontbase + 5	# this can be removed if necessary %]\r\n<FONT COLOR=\"white\" SIZE=\"[% constants.fontbase %]\">How Refreshing! ([% form.sid %]) </FONT>\r\n\r\n[% END %]\r\n<P>\r\n[% n = BLOCK; INCLUDE nextStory s=next; END %]\r\n[% p = BLOCK; INCLUDE nextStory s=prev; END %]\r\n[% IF p.length %] &lt;&nbsp; [% p %][% END %]\r\n[% IF n.length %] [% IF p.length %] | [% END %][% n %] &nbsp;&gt; [% END %]\r\n[% # ----------------- %]\r\n <P>&nbsp;</TD><TD>&nbsp;</TD><TD VALIGN=\"TOP\">\r\n[% # -- User\'s Article Box. %]\r\n[% IF user.is_anon; \r\n	# User is anonymous\r\n	contents = BLOCK;\r\n		INCLUDE userlogin\r\n			rootdir = constants.rootdir\r\n			return_url = \"${constants.rootdir}/article.pl?sid=$form.sid\";\r\n	END;\r\n	Slash.fancybox(constants.fancyboxwidth, \"$constants.sitename Login\", contents, 1, 1);\r\nELSE;\r\n 	# User is logged in.\r\n	M = BLOCK %][ <A HREF=\"[% constants.rootdir %]/users.pl?op=preferences\">Preferences</A>[% END %]\r\n	[% IF user.aseclev > 99 && user.aid;\r\n		M = BLOCK %]\r\n[% M %] | <A HREF=\"[% constants.rootdir %]/admin.pl\">Admin</A> |\r\n	  <A HREF=\"[% constants.rootdir %]/admin.pl?op=edit&sid=[% story.sid %]\">Editor</A>\r\n		[% END;\r\n	END;\r\n	M = \"$M ]<P>\\n\";\r\n	IF user.points || user.aseclev > 99;\r\n		M = BLOCK %]\r\n[% M %]<A HREF=\"[% constants.rootdir %]/users.pl\">You</A> have moderator access and \r\n<B>[% user.points %]</B> points. Welcome to those of you just joining: <B>please</B> \r\nread the <A HREF=\"[% constants.rootdir %]/moderation.shtml\">moderator guidelines</A>\r\nfor instructions. (<B>updated 9.9!</B>)\r\n\r\n<P>\r\n\r\n<LI>You can\'t post & moderate the same discussion.\r\n<LI>Concentrate on Promoting more than Demoting.\r\n<LI>Browse at -1 to keep an eye out for abuses.\r\n<LI><A HREF=\"mailto:[% constants.adminmail %]\">Mail the admin</A> URLs showing abuse \r\n(the cid link please!).\r\n		[% END;\r\n	END;\r\n	M = \"$M<P> $user.mylinks \";\r\n	# User can be logged in as both a regular user and author. This box should probably\r\n	# show both names to make the user aware.\r\n	name = user.nickname;\r\n	IF user.aid; name = \"$name &nbsp;($user.aid)\"; END;\r\n	Slash.fancybox(constants.fancyboxwidth, name, M, 0, 1);\r\nEND %]\r\n\r\n[%# ----------------------- PollBooth ------------------------- %]\r\n[% IF show_poll; Slash.fancybox(constants.fancyboxwidth, \'Article Poll\', poll, 0, 1); END %]\r\n\r\n[%# -------------------- Related Links ------------------------ %]\r\n[% Slash.fancybox(constants.fancyboxwidth, \'Related Links\', story.relatedtext, 0, 1) %]\r\n\r\n[%# ------------------- Section\'s Block ----------------------- %]\r\n[% IF section_block.length %]\r\n	[% Slash.fancybox(constants.fancyboxwidth, section.title, section_block, 0, 1) %]\r\n[% END %]\r\n</TD></TR><TR><TD COLSPAN=\"3\">\r\n\r\n[% BLOCK nextStory;\r\n	IF !s;\r\n		RETURN;\r\n	ELSIF s.title == story.title;\r\n		RETURN;\r\n	ELSE; \r\n		ls.link = s.title;\r\n		ls.sid = s.sid;\r\n		ls.section = s.section;\r\n		Slash.linkStory(ls);\r\n	END;\r\nEND %]',NULL,1000,'template','Template which describes the layout of the article display (and \r\nincluded comments if preferred).','[% # Define a useful block, first. %]\r\n[% BLOCK nextStory %]\r\n[% IF sign = \'<\';\r\n	cs = prev;\r\nELSE\r\n	cs = next;\r\nEND %]\r\n[% IF cs.title eq story.title %]\r\n	[% STOP %]\r\n[% ELSE \r\n	ls.link = cs.title;\r\n	ls.sid = cs.sid;\r\n	ls.section = cs.section;\r\n	Slash.linkStory(ls);\r\nEND %]\r\n[% END %]\r\n[% # Start of article_display %]\r\n<P>\r\n[% # -- articleMenu %]\r\n[% n = BLOCK; INCLUDE nextStory sign=\'>\'; END %]\r\n[% p = BLOCK; INCLUDE nextStory sign=\'<\'; END %]\r\n[% IF p.length %] &lt;&nbsp; [% p %][% END %]\r\n[% IF p.length %] | [% n %] &nbsp;&gt; [% END %]\r\n# -----------------\r\n</TD><TD>&nbsp;</TD><TD VALIGN=\"TOP\">\r\n[% # -- User\'s Article Box. %]\r\n[% IF user.is_anon; \r\n	# User is anonymous %]\r\n	contents = BLOCK;\r\n		# Assign expected variables.\r\n		rootdir = constants.rootdir;\r\n		return_url = \"$rootdir/article.pl?sid=$sid\";\r\n		INCLUDE widget_userlogin;\r\n	END; %]\r\n	[% Slash.fancybox(constants.fancyboxwidth, \"$constants.sitename Login\", contents) %]\r\n[% ELSE;\r\n 	# User is logged in.\r\n	M = \"[ <A HREF=\\\"$constants.rootdir/users.pl?op=preferences\\\">Preferences</A>\";\r\n	M .= \" | <A HREF=\\\"$constants.rootdir/admin.pl\\\">Admin</A> |\";\r\n	IF user.aseclev > 99 && user.aid;\r\n		M .= \"<A HREF=\\\"$constants.rootdir/admin.pl?op=edit&sid=$story.sid\\\">Editor</A> \";\r\n	END;\r\n	M .= \" ]<P>\\n\";\r\n	IF user.points || user.aseclev > 99;\r\n		M .= \"\r\n<A HREF=\\\"$constants.rootdir/users.pl\\\">You</A> have moderator access and \r\n<B>$user.points</B> points. Welcome to those of you just joining: <B>please</B> \r\nread the <A HREF=\\\"$constants.rootdir/moderation.shtml\\\">moderator guidelines</A>\r\nfor instructions. (<B>updated 9.9!</B>)\r\n\r\n<P>\r\n\r\n<LI>You can\'t post & moderate the same discussion.\r\n<LI>Concentrate on Promoting more than Demoting.\r\n<LI>Browse at -1 to keep an eye out for abuses.\r\n<LI><A HREF=\\\"mailto:$constants.adminmail\\\">Mail admin</A> URLs showing abuse \r\n(the cid link please!).\";\r\n	M .= \"<P> $user.mylinks \";\r\n%]\r\n	[% Slash.fancybox(constants.fancyboxwidth, user.aid || user.nickname, M) %]\r\n[% END %]\r\n# -- PollBooth\r\n[% IF show_pollquestion %][% Slash.pollbooth(form.sid) %][% END %]\r\n# -- Related Links\r\n[% Slash.fancybox(constants.fancyboxwidth, \'Related Links\', story.relatedtext) %]\r\n# -- Section\'s Block\r\n[% IF section_block.length %]\r\n	[% Slash.fancybox(constants.fancyboxwidth, section.title, section_block) %]\r\n[% END %]\r\n</TD></TR><TR><TD COLSPAN=\"3\">\r\n# -- Comments.\r\n[% Slash.printComments(form.sid) %]\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('comments-errors','<!-- [% type %] -->\r\n[% SWITCH type;\r\n\r\n# LOGIN ERROR.\r\nCASE \"login error\" %]\r\n<P><B>Login for \"[% nickname %]\" has failed</B>. Please try again. [% op %]<BR><P>\r\n[%\r\n\r\n# NO ANONYMOUS POSTING.\r\nCASE \"no anonymous posting\" %]\r\nSorry, anonymous posting has been turned off. Please \r\n<A HREF=\"[% rootdir %]/users.pl\">register and log in</A>.\r\n[%\r\n\r\n# ALREADY POSTED.\r\nCASE \"already posted\" %]\r\nYou\'ve already posted something in this discussion<BR>\r\n[%\r\n\r\n# ANONYMOUS DISALLOWED.\r\nCASE \"anonymous disallowed\" %]\r\nSorry, anonymous posting has been turned off.\r\nPlease <A HREF=\"[% rootdir %]/users.pl\">register and log in</A>.\r\n[%\r\n\r\n# NO BODY.\r\nCASE \"no body\" %]\r\nCat got your tongue? (something important seems to be missing from your\r\ncomment ... like the body or the subject!)\r\n[%\r\n\r\n# NO POINTS.\r\nCASE \"no points\" %]\r\nYou don\'t have any moderator points.<BR>\r\n[%\r\n\r\n# MAX POSTS.\r\nCASE \"max posts\" %]\r\n<P><B>Limit reached! The maximum number of comments you can post in a day is [% max_posts %].\r\n</B></P>\r\n[%\r\n\r\n# TROLL MESSAGE.\r\nCASE \"troll message\" %]\r\nThis account or IP has been temporarily disabled. This means that either\r\nthis IP or user account has been moderated down more than 5 times in the\r\nlast 24 hours.  If you think this is unfair, you should contact\r\n[% admin_main %].  If you are being a troll, now is the time for you to\r\neither grow up, or change your IP.\r\n[%\r\n\r\n# NESTING TOO DEEP\r\nCASE \"nesting too deep\" %]\r\nYou can only post nested lists and blockquotes four levels deep.\r\nPlease fix your UL, OL, and BLOCKQUOTE tags.\r\n[%\r\n\r\n# LOW WORDS-PER-LINE\r\nCASE \"low words-per-line\" %]\r\nYour comment has too few words per line (currently [% ratio %]).<BR>\r\n[%\r\n\r\n# FILTER MESSAGE.\r\nCASE \"filter message\" %]\r\n<BR>Lameness filter encountered.  Post aborted.<BR><BR>\r\n[% IF err_message %]\r\n<B>[% err_message %]</B><BR>\r\n[% END %]\r\n[%\r\n\r\n# COMPRESS FILTER.\r\nCASE \"compress filter\" %]\r\nLameness filter encountered. Your comment violated the [% ratio %] compression filter.\r\n<B>Comment aborted</B>\r\n[%\r\n\r\n# SUBMISSION ERROR.\r\nCASE \"submission error\" %]\r\n<P>There was an unknown error in the submission.<BR>\r\n\r\n[%\r\n\r\n# MAXCID EXCEEDED.\r\nCASE \"maxcid exceeded\" %]\r\nDon\'t you have anything better to do with your life?<BR>\r\n[%\r\n\r\n# VALIDATION ERROR. (possible dup? not quite sure about this one...)\r\nCASE \"validation error\" %]\r\nSomething is wrong: parent=[% form.pid %] dups=[% dups %] discussion=[% form.sid %]\r\n<UL>\r\n[% IF form.postersubj %]\r\n	<LI>Didja forget a subject?</LI>\r\n[% END %]\r\n[% IF dups %]\r\n	<LI>Duplicate.  Did you submit twice?</LI>\r\n[% END %]\r\n[% UNLESS form.sid %]\r\n	<LI>Space aliens have eaten your data.</LI>\r\n[% END %]\r\n	<LI>Let us know if anything exceptionally strange happens</LI>\r\n</UL>\r\n[%\r\n\r\n# DEFAULT (an Unexpected or Unknown error condition)\r\nCASE %]\r\nAn unexpected error has occured.<BR><B>[% value %]</B><BR>\r\n[% END %]\r\n',NULL,1000,'template','Template which contains most if not all of the error messages \r\ngenerated by the \"comments\" script.','[% SWITCH type;\r\n\r\n# LOGIN ERROR.\r\nCASE \"login error\" %]\r\n\r\n\r\n# DEFAULT (an Unexpected or Unknown error condition)\r\nCASE %]\r\nAn unexpected error has occured.<BR><B>[% value %]</B><BR>\r\n[% END %]\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('comments-moderation','[% SWITCH type;\r\n\r\n# ALREADY MODERATED.\r\nCASE \"already moderated\" %]\r\n<LI>[% subject %]([% sid %]-[% cid %], <B>Already moderated</B>)</LI>\r\n[%\r\n\r\n# SCORE LIMIT.\r\nCASE \"score limit\" %]\r\n<LI>[% subject %] ([% sid %]-[% cid %], <B>Comment already at limit</B>)</LI>\r\n[%\r\n\r\n# (COMMENT SUCCESSFULLY) MODERATED.\r\nCASE \"moderated\" %]\r\n<LI>[% val %] ([% reason %]) [% subject %] ([% sid %]-[% cid %], <B>[% points %]</B> points left)\r\n</LI>\r\n[% END %]',NULL,1000,'template','Template which determines output for moderation results.\r\n','[% SWITCH type;\r\n\r\n# ALREADY MODERATED.\r\nCASE \"already moderated\" %]\r\n<LI>[% subject %]([% sid %]-[% cid %], <B>Already moderated</B>)</LI>\r\n[%\r\n\r\n# SCORE LIMIT.\r\nCASE \"score limit\" %]\r\n<LI>[% subject %] ([% sid %]-[% cid %], <B>Comment already at limit</B>)</LI>\r\n[%\r\n\r\n# (COMMENT SUCCESSFULLY) MODERATED.\r\nCASE \"moderated\" %]\r\n<LI>[% val %] ([% reason %]) [% subject %] ([% sid %]-[% cid %], <B>[% points %]</B> points left)\r\n</LI>\r\n[% END %]','comments',NULL,'Comments-Moderation Results',NULL,'','',0);
INSERT INTO blocks VALUES ('comments-comment-submitted','Comment Submitted. There will be a delay before the comment becomes part of the static page.\r\nWhat you submitted appears below.  If there is a mistake...well, you should have used the \r\n\'Preview\' button!<P>',NULL,1000,'template','Message displayed when a user submits a comment.','Comment Submitted. There will be a delay before the comment becomes part of the static page.\r\nWhat you submitted appears below.  If there is a mistake...well, you should have used the \r\n\'Preview\' button!<P>','comments',NULL,'Comment Submit Message',NULL,'','',0);
INSERT INTO blocks VALUES ('comments-deleted-cids','The following \r\n[% IF count > 1 %][% count %] comments[% ELSE %]comment[% END %] were deleted from [% sid%]:\r\n[% FOR c = comments_deleted %]\r\n#[% c %][% UNLESS c == comments_deleted.last %], [% END %]\r\n[% END %]\r\n<BR>',NULL,1000,'template','Message that is displayed when comments are deleted from a discussion.','The following comments were deleted from [% sid%]:\r\n[% FOR c = comments_deleted %]\r\n[% c %][% UNLESS c = comments_deleted.last %], [% END %]\r\n[% END %]','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('comments-deleted-message','[% total_deleted %] comments deleted. Comment count set to [% comment_count %]<BR>\r\n',NULL,1000,'template','Message printed when comments have been deleted from a discussion.','[% total_deleted %] comments deleted. Comment count set to [% comment_count %]<BR>\r\n','comments',NULL,'Comments Deteted Message',NULL,'','',0);
INSERT INTO blocks VALUES ('comments-discussion-list','<MULTICOLS COLS=2>\r\n[% FOR d = discussions %]\r\n	<LI><A HREF=\"[% rootdir %]/comments.pl?sid=[% d.0 %]\">\r\n[% IF d.1.defined && d.1.length %][% d.1 %][% ELSE %]untitled[% END %]</A>\r\n	(<A HREF=\"[% d.2 %]\">referer</A>)\r\n[% END %]\r\n</MULTICOL>\r\n\r\n',NULL,100,'template','This template controls how the Discussion List is printed. For default \r\nSlashcode, this list is printed when comments.pl executed with no \r\narguments.','<MULTICOLS COLS=2>\r\n[% FOR d = discussions %]\r\n	[% DEFAULT d.1 = \'untitled\' %]\r\n	<LI><A HREF=\"[% rootdir %]/comments.pl?sid=[% d.0 %]\">[% d.1 %]</A>\r\n	(<A HREF=\"[d.2]\">referer</A>)\r\n[% END %]\r\n</MULTICOL>\r\n\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('comments-edit-comment','[% USE Slash %]\r\n[% IF form.pid %]\r\n  [% INCLUDE titlebar width=\"95%\" title=\" $reply.subject\" %]\r\n  <TABLE BORDER=\"0\" CELLPADDING=\"0\" CELLSPACING=\"0\" WIDTH=\"95%\" ALIGN=\"CENTER\">\r\n  [% Slash.dispComment(reply) %]\r\n  </TABLE><P>\r\n[% END %]\r\n[% IF preview %]\r\n  [% INCLUDE titlebar width=\"95%\" title=\"Preview Comment\" %]\r\n  [% preview %]\r\n  <P>\r\n\r\n[% END %]\r\n  [% INCLUDE titlebar width=\"95%\" title=\"Post Comment\" %]\r\n\r\n[% IF error_message %][% error_message %][% END %]\r\n\r\n<FORM ACTION=\"[% rootdir %]/comments.pl\" METHOD=\"POST\">\r\n\r\n	<INPUT TYPE=\"HIDDEN\" NAME=\"sid\" VALUE=\"[% form.sid %]\">\r\n	<INPUT TYPE=\"HIDDEN\" NAME=\"pid\" VALUE=\"[% form.pid %]\">\r\n        <INPUT TYPE=\"HIDDEN\" NAME=\"mode\" VALUE=\"[% user.mode %]\">\r\n	<INPUT TYPE=\"HIDDEN\" NAME=\"startat\" VALUE=\"[% user.startat %]\">\r\n	<INPUT TYPE=\"HIDDEN\" NAME=\"threshold\" VALUE=\"[% user.threshold %]\">\r\n	<INPUT TYPE=\"HIDDEN\" NAME=\"commentsort\" VALUE=\"[% user.commentsort %]\">\r\n[% IF form.formkey %]\r\n	<INPUT TYPE=\"HIDDEN\" NAME=\"formkey\" VALUE=\"[% form.formkey %]\">\r\n[% END %]\r\n\r\n	<TABLE BORDER=\"0\" CELLSPACING=\"0\" CELLPADDING=\"1\">\r\n[% userlink = \'\' %]\r\n[% IF user.is_anon %]\r\n	[% userlink = \"<A HREF=\\\"$rootdir/users.pl?op=userclose\\\">Log Out</A>\" %]\r\n	<TR><TD> </TD><TD>\r\n		You are not logged in.  You can login now using the\r\n		convenient form below, or\r\n		<A HREF=\"[% rootdir %]/users.pl\">Create an Account</A>.\r\n		Posts without proper registration are posted as\r\n		<B>[% user.nickname %]</B>\r\n	</TD></TR>\r\n	<INPUT TYPE=\"HIDDEN\" NAME=\"rlogin\" VALUE=\"userlogin\">\r\n\r\n	<TR><TD ALIGN=\"RIGHT\">Nick</TD><TD>\r\n		<INPUT TYPE=\"TEXT\" NAME=\"unickname\" VALUE=\"[% form.nickname %]\">\r\n\r\n	</TD></TR><TR><TD ALIGN=\"RIGHT\">Passwd</TD><TD>\r\n		<INPUT TYPE=\"PASSWORD\" NAME=\"upasswd\">\r\n\r\n	</TD></TR>\r\n[% ELSE;\r\n	userlink = \"<A HREF=\\\"$rootdir/users.pl\\\">Create Account</A>\" %]\r\n[% END %]\r\n\r\n	<TR><TD WIDTH=\"130\" ALIGN=\"RIGHT\">Name: </TD><TD WIDTH=\"500\">\r\n		<A HREF=\"[% rootdir %]/users.pl\">[% user.nickname %]</A>[ [% userlink %] ]</TD></TR>\r\n[% IF user.fakeemail %]\r\n	<TR><TD ALIGN=\"RIGHT\">Email: </TD>\r\n	    <TD>[% user.fakeemail %]</TD></TR>\r\n[% END %]\r\n[% IF user.homepage %]\r\n	<TR><TD ALIGN=\"RIGHT\">URL</TD>\r\n		<TD><A HREF=\"[% user.homepage %]\">[% user.homepage %]</A></TD></TR>\r\n[% END %]\r\n\r\n	<TR><TD ALIGN=\"RIGHT\">Subject: </TD>\r\n		<TD>[% cgi.textfield(\'postersubj\', form.postersubj, 50, 50) %]</TD></TR>\r\n\r\n	<TR>\r\n		<TD ALIGN=\"RIGHT\" VALIGN=\"TOP\">Comment</TD>\r\n		<TD><TEXTAREA WRAP=\"VIRTUAL\" NAME=\"postercomment\" ROWS=\"10\"\r\n		COLS=\"50\">[% Slash.stripByMode(form.postercomment, \'literal\') %]</TEXTAREA>\r\n		<BR>(Use the Preview Button! Check those URLs!\r\n		Don\'t forget the http://!)\r\n	</TD></TR>\r\n\r\n	<TR><TD> </TD><TD>\r\n	\r\n[% IF !user.is_anon && user.karma > goodkarma %]\r\n		[% cgi.checkbox(\'nobonus\', form.nobonus) %] No Score +1 Bonus\r\n[% END %]\r\n[% IF allow_anonymous && user.karma > -1 && !user.isanon %]\r\n		[% cgi.checkbox(\'postanon\', form.postanon) %] Post Anonymously<BR>\r\n[% END %]\r\n		<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"Submit\">\r\n		<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"Preview\">\r\n		[% format_select %]\r\n	</TD></TR><TR>\r\n		<TD VALIGN=\"TOP\" ALIGN=\"RIGHT\">Allowed HTML: </TD><TD><FONT SIZE=\"1\">\r\n		[% approved_tags %]\r\n		</FONT>\r\n	</TD></TR>\r\n</TABLE>\r\n\r\n</FORM>\r\n\r\n<B>Important Stuff:</B>\r\n	<LI>Please try to keep posts on topic.\r\n	<LI>Try to reply to other people comments instead of starting new threads.\r\n	<LI>Read other people\'s messages before posting your own to avoid simply duplicating \r\n		what has already been said.\r\n	<LI>Use a clear subject that describes what your message is about.\r\n	<LI>Offtopic, Inflammatory, Inappropriate, Illegal, or Offensive comments might be \r\n		moderated. (You can read everything, even moderated posts, by adjusting your \r\n		threshold on the User Preferences Page)\r\n\r\n<P><FONT SIZE=\"2\">Problems regarding accounts or comment posting should be sent to\r\n	<A HREF=\"mailto:[% admin_mail %]\">[% admin_name %]</A>.</FONT>\r\n\r\n\r\n',NULL,1000,'template','Template which renders the comment editor.','[% USE Slash %]\r\n[% IF form.pid %]\r\n[% titlebar(\"95%\", \" $reply.subject\") %]\r\n<TABLE BORDER=\"0\" CELLPADDING=\"0\" CELLSPACING=\"0\" WIDTH=\"95%\" ALIGN=\"CENTER\">\r\n[% dispComment(reply) %]\r\n</TABLE><P>\r\n\r\n[% END %]\r\n[% IF error_message %][% error_message %][% END %]\r\n\r\n','',NULL,NULL,NULL,NULL,NULL,0);
