INSERT INTO blocks VALUES ('comments-moderation-header','\r\n<UL>\r\n\r\n',NULL,1000,'template','This block is displayed right before moderation occurs.','\r\n<UL>\r\n\r\n','comments',NULL,'Comment Moderation Header',NULL,'','',0);
INSERT INTO blocks VALUES ('comments-preview-comment','[% USE Slash %]\r\n<TABLE BORDER=\"0\" CELLPADDING=\"0\" CELLSPACING=\"0\" WIDTH=\"95%\" ALIGN=\"CENTER\">\r\n[% Slash.dispComment(preview) %]\r\n</TABLE>       \r\n\r\n',NULL,1000,'template','Template which describes how the comment preview form is displayed.\r\n','[% USE Slash %]\r\n<TABLE BORDER=\"0\" CELLPADDING=\"0\" CELLSPACING=\"0\" WIDTH=\"95%\" ALIGN=\"CENTER\">\r\n[% dispComment(preview) %]\r\n</TABLE>       \r\n\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('comments-sig-prefix','','',1000,'template','Content that appears before any user signature in a comment.','','comments',0,'User Signature Prefix',0,'','',0);
INSERT INTO blocks VALUES ('comments-undo-moderation','[% FOR r = removed %]\r\nUndoing moderation to Comment #[% r %]<BR>\r\n[% END %]',NULL,1000,'template','Template which displays a message indicating moderation undone for\r\nevery item in the template variable \'removed\'.','[% FOR r = removed %]\r\nUndoing moderation to Comment #[% r %]<BR>\r\n[% END %]','comments',NULL,'Comments-Undo Moderation Message',NULL,'','',0);
INSERT INTO blocks VALUES ('metamod-display','[% USE Slash %]\r\n[% INCLUDE titlebar width=\"99%\" title=\"Meta Moderation\" %]\r\n[% IF !comments.size %]\r\n<BIG><CENTER>No comments available for meta moderation!</CENTER></BIG>\r\n[% STOP %]\r\n[% END %]\r\n[% user.noscores = 1; # Do not show scores in comment display. %]\r\n<B>PLEASE READ THE DIRECTIONS CAREFULLY BEFORE EMAILING\r\n[% constants.siteadmin_name.uc %]!</B> <P>What follows is [% constants.m2_comments %] random \r\nmoderations performed on comments in the last few weeks on [% constants.sitename %]. \r\nYou are asked to <B>honestly</B> evaluate the actions of the moderator of each\r\ncomment. Moderators who are ranked poorly will cease to be eligible for\r\nmoderator access in the future.\r\n\r\n<UL>\r\n\r\n<LI>If you are confused about the context of a particular comment, just\r\nlink back to the comment page through the parent link, or the #XXX cid\r\nlink.</LI>\r\n\r\n<LI><B><FONT SIZE=\"5\">Duplicates are fine</FONT></B> (Big because over\r\n<B>100</B> people have emailed me to tell me about this even though it is\r\nexplained <B>right here</B>.)  You are not moderating a \"Comment\" you\r\nare moderating a \"Moderation\".  Therefore, if a comment is moderated\r\nmore than once, it can appear multiple times below.  Don\'t worry about it.</LI>\r\n\r\n<LI>If you are unsure, feel free to leave it unchanged.</LI>\r\n\r\n<LI>Please read the <A HREF=\"[% constants.rootdir %]/moderation.shtml\">Moderator Guidelines</A>\r\nand try to be impartial and fair.  You are not moderating to make your\r\nopinions heard, you are trying to help promote a rational discussion. \r\nPlay fairly and help make [% constants.sitename %] a little better for everyone.</LI>\r\n\r\n<LI>Scores and information identifying the posters of these comments have been\r\nremoved to help prevent bias in meta moderation. If you really need to know, \r\nyou can click through and see the original message, but we encourage you not \r\nto do this unless you need more context to fairly meta moderate.</LI> \r\n\r\n</UL>\r\n\r\n<FORM ACTION=\"[% constants.rootdir %]/metamod.pl\" METHOD=\"POST\">\r\n<TABLE>\r\n[% FOR C = comments %]\r\n[% \r\n	Slash.dispComment(C);\r\n	S.link = C.title; S.sid = C.sid;\r\n%]\r\n	<TR><TD>\r\n		Story: <B>[% Slash.linkStory(S) %]</B><BR> \r\n                Rating: <B>[% constants.reasons.${C.modreason} %]</B>.<BR>\r\n		This rating is <B>Unfair\r\n			<INPUT TYPE=\"RADIO\" NAME=\"mm[% C.id %]\" VALUE=\"-\">\r\n			<INPUT TYPE=\"RADIO\" NAME=\"mm[% C.id %]\" VALUE=\"0\" CHECKED>\r\n			<INPUT TYPE=\"RADIO\" NAME=\"mm[% C.id %]\" VALUE=\"+\">\r\n		Fair</B><HR>\r\n	</TD></TR>\r\n[% END %]\r\n</TABLE>\r\n\r\n<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"MetaModerate\">\r\n\r\n</FORM>\r\n',NULL,1000,'template','Template that describes the meta moderation screen, which shows the\r\ncomments and the metamoderation options, to the user.','[% USE Slash %]\r\n[% Slash.titlebar(\"99%\",\"Meta Moderation\") %]\r\n[% u.noscores = 1; # Do not show scores in comment display. %]\r\n<B>PLEASE READ THE DIRECTIONS CAREFULLY BEFORE EMAILING\r\n\\U[% admin_name %]\\E!</B> <P>What follows is [% c.m2_comments %] random \r\nmoderations performed on comments in the last few weeks on [% c.sitename %]. \r\nYou are asked to <B>honestly</B> evaluate the actions of the moderator of each\r\ncomment. Moderators who are ranked poorly will cease to be eligible for\r\nmoderator access in the future.\r\n\r\n<UL>\r\n\r\n<LI>If you are confused about the context of a particular comment, just\r\nlink back to the comment page through the parent link, or the #XXX cid\r\nlink.</LI>\r\n\r\n<LI><B><FONT SIZE=\"5\">Duplicates are fine</FONT></B> (Big because over\r\n<B>100</B> people have emailed me to tell me about this even though it is\r\nexplained <B>right here</B>.)  You are not moderating a \"Comment\" you\r\nare moderating a \"Moderation\".  Therefore, if a comment is moderated\r\nmore than once, it can appear multiple times below.  Don\'t worry about it.</LI>\r\n\r\n<LI>If you are unsure, feel free to leave it unchanged.</LI>\r\n\r\n<LI>Please read the <A HREF=\"[% c.rootdir %]/moderation.shtml\">Moderator Guidelines</A>\r\nand try to be impartial and fair.  You are not moderating to make your\r\nopinions heard, you are trying to help promote a rational discussion. \r\nPlay fairly and help make [% c.sitename %] a little better for everyone.</LI>\r\n\r\n<LI>Scores and information identifying the posters of these comments have been\r\nremoved to help prevent bias in meta moderation. If you really need to know, \r\nyou can click through and see the original message, but we encourage you not \r\nto do this unless you need more context to fairly meta moderate.</LI> \r\n\r\n</UL>\r\n\r\n<FORM ACTION=\"[% script_name %]\" METHOD=\"POST\">\r\n<TABLE>\r\n[% FOR C = comments %]\r\n[% \r\n	Slash.dispComment(C);\r\n	S.link = C.title; S.sid = C.sid;\r\n	Slash.linkStory(S);\r\n%]\r\n	<TR><TD>\r\n		Story:<B>%s</B><BR> Rating: <B>[% c.reasons.$C.modreason %]</B>.<BR>\r\n		This rating is <B>Unfair\r\n			<INPUT TYPE=\"RADIO\" NAME=\"mm[% C.id %]\" VALUE=\"-\">\r\n			<INPUT TYPE=\"RADIO\" NAME=\"mm[% C.id %]\" VALUE=\"0\" CHECKED>\r\n			<INPUT TYPE=\"RADIO\" NAME=\"mm[% C.id %]\" VALUE=\"+\">\r\n		Fair</B><HR>\r\n	</TD></TR>\r\n[% END %]\r\n</TABLE>\r\n\r\n<INPUT TYPE=\"SUBMIT\" NAME=\"op\" VALUE=\"MetaModerate\">\r\n\r\n</FORM>\r\n','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('metamod-eligibility-tests','[% IF user.is_anon %]\r\nYou are not logged in.\r\n[% ELSIF user.uid > (user_count * 9) # user_count * constants.m2_userpercentage %]\r\nYou haven\'t been a [% constants.sitename %] user long enough.\r\n[% ELSIF user.karma < 0 %]\r\nYour karma is insufficient for this task.\r\n[% ELSIF last.lastmm == \'0\' %]\r\nYou have recently meta moderated.\r\n[% ELSE %]\r\nEligible\r\n[% END %]',NULL,1000,'template','This template accepts user data and site data and determines if a user \r\nis currently eligible to metamoderate. If a user is not eligible, this\r\ntemplate will return a message describing why, if a user is eligible, \r\nthis template returns \"Eligible\".','[% IF user.is_anon %]\r\nYou are not logged in.\r\n[% ELSIF user.uid > user_count * c.m2_userpercentage %]\r\nYou haven\'t been a c.sitename user long enough.\r\n[% ELSIF user.karma < 0 %]\r\nYour karma is insufficient for this task.\r\n[% ELSIF last.lastmm == \'0\' %]\r\nYou have recently meta moderated.\r\n[% ELSE %]\r\nEligible\r\n[% END %]','',NULL,NULL,NULL,NULL,NULL,0);
INSERT INTO blocks VALUES ('metamod-not-eligible','<BR>You are currently not eligible to Meta Moderate.<BR>\r\nReturn to <A HREF=\"[% rootdir %]/\">the [% sitename %] homepage</A>.<BR>\r\n',NULL,1000,'template','Template which describe the message that is printed when a user \r\nwho is ineligible tried to metamoderate.','<BR>You are currently not eligible to Meta Moderate.<BR>\r\nReturn to <A HREF=\"[% rootdir %]/\">the [% sitename %] homepage</A>.<BR>\r\n','',NULL,'Inability To Metamoderate Message',NULL,'','',0);
INSERT INTO blocks VALUES ('metamod-results','[% IF aseclev > 10 %]<UL>[% FOR c = changes %]\r\n<LI>Updating UID #[% c.0 %] for [% c.1 %]\r\n[% END %]</UL>[% END %]\r\n[% count %] comments have been meta moderated.  Thanks for participating.\r\nYou may wanna go back <A HREF=\"[% rootdir %]/\">home</A> or perhaps to\r\n<A HREF=\"[% rootdir %]/users.pl\">your user page</A>.\r\n[% IF aseclev > 10 %]<BR>Total unfairs is [% metamod.unfair %][% END %]',NULL,1000,'template','Template that describes the message that is printed after a user \r\nmetamoderates.','[% count %] comments have been meta moderated.  Thanks for participating.\r\nYou may wanna go back <A HREF=\"[% rootdir %]/\">home</A> or perhaps to\r\n<A HREF=\"[% rootdir %]/users.pl\">your user page</A>.\r\n','',NULL,NULL,NULL,NULL,NULL,0);
