INSERT INTO blocks VALUES ('edit_filter','<TABLE BORDER=\"0\">\r\n        <TR>\r\n                <TD VALIGN=\"top\" WIDTH=\"40\"><B>Filter id</B><BR>This is not editable<BR><BR></TD>\r\n                <TD VALIGN=\"top\"><INPUT TYPE=\"hidden\" name=\"filter_id\" value=\"$filter_id\">&nbsp;&nbsp;</TD>\r\n                <TD VALIGN=\"top\"> $filter_id </TD><TD>&nbsp;</TD>\r\n        </TR>\r\n        <TR>\r\n                <TD VALIGN=\"top\" WIDTH=\"40\"><B> Regex</B><BR>This is the base regex that you use for the filter.<BR><BR></TD>\r\n                <TD>&nbsp;&nbsp;</TD><TD VALIGN=\"top\"><FONT FACE=\"courier\" SIZE=\"+1\"><BR> $regex </FONT></TD>\r\n                <TD VALIGN=\"top\"><INPUT TYPE=\"text\" SIZE=\"30\" NAME=\"regex\" VALUE=\"$regex\"></TD>\r\n        </TR>\r\n        <TR>\r\n                <TD VALIGN=\"top\"><B> Modifier </B><BR>The modifier for the regex /xxx/gi /xxx/g /xxx/<BR><BR></TD>\r\n                <TD>&nbsp;&nbsp;</TD><TD VALIGN=\"top\"><BR> $modifier </TD>\r\n                <TD VALIGN=\"top\"><INPUT TYPE=\"text\" SIZE=\"4\" NAME=\"modifier\" VALUE=\"$modifier\"></TD>\r\n        </TR>\r\n        <TR>\r\n                <TD VALIGN=\"top\" WIDTH=\"40\"><B> Field </B><BR>\r\n                This is the field you want to check. Refer to the code to make\r\n                sure you have the correct fieldname.\r\n                <BR><BR>\r\n                </TD><TD>&nbsp;&nbsp;</TD>\r\n                <TD VALIGN=\"top\"><BR> $field </TD>\r\n                <TD VALIGN=\"top\"><INPUT TYPE=\"text\" NAME=\"field\" VALUE=\"$field\"></TD>\r\n        </TR>\r\n        <TR>\r\n                <TD VALIGN=\"top\" WIDTH=\"40\">\r\n                <B> Ratio </B><BR>\r\n                The percentage of the fieldsize that you want the regex to match.\r\n                This is used to calculate the number of instances for the regex.\r\n                For instance, if the ration is .50, and the comment size is 100, \r\n                 then the regex ends up becoming /xxx{50,}/. Note: if this value is > 0,\r\n                 then you cannot use the minimum match field.\r\n                <BR><BR>\r\n                </TD>\r\n                <TD>&nbsp;&nbsp;</TD><TD VALIGN=\"top\"><BR> $ratio </TD>\r\n                <TD VALIGN=\"top\"><INPUT TYPE=\"text\" SIZE=\"8\" NAME=\"ratio\" VALUE=\"$ratio\"></TD>\r\n        </TR>\r\n        <TR>\r\n                <TD VALIGN=\"top\" WIDTH=\"40\"><B> Minimum match </B><BR> \r\n                This is the hardcoded minimum for the regex, if you\'re not using a ratio.\r\n                 For instance, if you set this to 10, your regex becomes /xxx/{10,}.\r\n                Note: You can\'t use ratio if you have this set to anything greater than 0\r\n                <BR><BR>\r\n                </TD>\r\n                <TD>&nbsp;&nbsp;</TD><TD VALIGN=\"top\"><BR> $minimum_match </TD>\r\n                <TD VALIGN=\"top\"><INPUT TYPE=\"text\" SIZE=\"8\" NAME=\"minimum_match\" VALUE=\"$minimum_match\"></TD>\r\n        </TR>\r\n        <TR>\r\n                <TD VALIGN=\"top\" WIDTH=\"40\"><B> Minimum length</B>\r\n                <BR>This is the minimum length of the comment in order for the filter to apply.\r\n                If set to zero, there will be no minimum size length.\r\n                <BR><BR>\r\n                </TD>\r\n                <TD>&nbsp;&nbsp;</TD><TD VALIGN=\"top\"><BR> $minimum_length </TD>\r\n                <TD VALIGN=\"top\"><INPUT TYPE=\"text\" SIZE=\"8\" NAME=\"minimum_length\" VALUE=\"$minimum_length\"></TD>\r\n        </TR>\r\n        <TR>\r\n                <TD VALIGN=\"top\" WIDTH=\"40\"><B>Maximum length </B>\r\n                <BR>This is the maximum length a comment can be in order for the filter to apply.\r\n                If left to zero, there will be no maximum size length.\r\n                <BR><BR>\r\n                </TD>\r\n                <TD>&nbsp;&nbsp;</TD><TD VALIGN=\"top\"><BR> $maximum_length </TD>\r\n                <TD VALIGN=