INSERT INTO blocks VALUES ('pollBooth-vote','<CENTER><TABLE BORDER=\"0\" CELLPADDING=\"2\" CELLSPACING=\"0\" WIDTH=\"500\">\r\n	<TR><TD> </TD><TD COLSPAN=\"1\">\r\n\r\n[% INCLUDE titlebar %]\r\n\r\n<FONT SIZE=\"2\">[% notes %]</FONT></TD></TR>\r\n\r\n[% FOR poll = pollitems %]\r\n\r\n    [% answer     = poll.0\r\n       imagewidth = poll.1\r\n       votes      = poll.2\r\n       percent    = poll.3\r\n    %]\r\n\r\n	<TR>\r\n		<TD WIDTH=\"100\" ALIGN=\"RIGHT\">[% answer %] &nbsp;</TD>\r\n		<TD WIDTH=\"450\"><NOBR><IMG\r\n			SRC=\"[% constants.imagedir %]/leftbar.gif\" WIDTH=\"4\" HEIGHT=\"20\" ALT=\"\"><IMG\r\n			SRC=\"[% constants.imagedir %]/mainbar.gif\" HEIGHT=20\r\n			width=\"[% imagewidth %]\" ALT=\"[% percent %]%\"><IMG\r\n			SRC=\"[% constants.imagedir %]/rightbar.gif\" WIDTH=\"4\" HEIGHT=\"20\" ALT=\"\">\r\n			[% votes %] /\r\n			<FONT COLOR=\"#006666\">[% percent %]%</FONT></NOBR>\r\n		</TD>\r\n	</TR>\r\n\r\n[% END %]\r\n\r\n	<TR><TD COLSPAN=\"2\" ALIGN=\"RIGHT\">\r\n		<FONT SIZE=\"4\"><B>[% voters %] total votes.</B></FONT>\r\n	</TD></TR><TR><TD COLSPAN=\"2\"><P ALIGN=\"CENTER\">\r\n		[\r\n			<A HREF=\"[% env.script_name %]?qid=[% qid %]\">Voting Booth</A> |\r\n			<A HREF=\"[% env.script_name %]\">Other Polls</A> |\r\n			<A HREF=\"[% constants.rootdir %]/\">Back Home</A>\r\n		]\r\n	</TD></TR><TR><TD COLSPAN=\"2\">[% postvote %]</TD></TR>\r\n</TABLE></CENTER>\r\n\r\n',NULL,10000,'template','','<CENTER><TABLE BORDER=\"0\" CELLPADDING=\"2\" CELLSPACING=\"0\" WIDTH=\"500\">\r\n	<TR><TD> </TD><TD COLSPAN=\"1\">\r\n\r\n[% Slash.display(\'titlebar\') %]\r\n\r\n<FONT SIZE=\"2\">[% notes %]</FONT></TD></TR>\r\n\r\n[% FOR poll = pollitems %]\r\n\r\n    [% answer     = poll.0\r\n       imagewidth = poll.1\r\n       votes      = poll.2\r\n       percent    = poll.3\r\n    %]\r\n\r\n	<TR>\r\n		<TD WIDTH=\"100\" ALIGN=\"RIGHT\">[% answer %] &nbsp;</TD>\r\n		<TD WIDTH=\"450\"><NOBR><IMG\r\n			SRC=\"[% constants.imagedir %]/leftbar.gif\" WIDTH=\"4\" HEIGHT=\"20\" ALT=\"\"><IMG\r\n			SRC=\"[% constants.imagedir %]/mainbar.gif\" HEIGHT=20\r\n			width=\"[% imagewidth %]\" ALT=\"[% percent %]%\"><IMG\r\n			SRC=\"[% constants.imagedir %]/rightbar.gif\" WIDTH=\"4\" HEIGHT=\"20\" ALT=\"\">\r\n			[% votes %] /\r\n			<FONT COLOR=\"#006666\">[% percent %]%</FONT></NOBR>\r\n		</TD>\r\n	</TR>\r\n\r\n[% END %]\r\n\r\n	<TR><TD COLSPAN=\"2\" ALIGN=\"RIGHT\">\r\n		<FONT SIZE=\"4\"><B>[% voters %] total votes.</B></FONT>\r\n	</TD></TR><TR><TD COLSPAN=\"2\"><P ALIGN=\"CENTER\">\r\n		[\r\n			<A HREF=\"[% env.script_name %]?qid=[% qid %]\">Voting Booth</A> |\r\n			<A HREF=\"[% env.script_name %]\">Other Polls</A> |\r\n			<A HREF=\"[% constants.rootdir %]/\">Back Home</A>\r\n		]\r\n	</TD></TR><TR><TD COLSPAN=\"2\">[% postvote %]</TD></TR>\r\n</TABLE></CENTER>\r\n\r\n','',0,'',NULL,'','',0);
INSERT INTO blocks VALUES ('pollbooth','<FORM ACTION=\"[% env.script_name %]\">\r\n	<INPUT TYPE=\"hidden\" NAME=\"qid\" VALUE=\"[% qid %]\">\r\n    [% IF constants.currentSection %]\r\n	<INPUT TYPE=\"hidden\" NAME=\"section\" VALUE=\"[% constants.currentSection %]\">\r\n    [% END %]\r\n<B>[% question %]</B>\r\n\r\n\r\n[% FOREACH poll = polls %]\r\n    [% answer   = poll.1\r\n       aid      = poll.2\r\n    %]\r\n	<BR><INPUT TYPE=\"radio\" NAME=\"aid\" VALUE=\"[% aid %]\">[% answer %]\r\n[% END %]\r\n\r\n<BR><INPUT TYPE=\"submit\" VALUE=\"Vote\">\r\n[ <A HREF=\"[% env.script_name %]?[% sect %]qid=[% qid %]&aid=[% constants.anonymous_coward_uid %]\"><B>Results</B></A> |\r\n<A HREF=\"[% env.script_name %]?[% sect %]\"><B>Polls</B></A> ] <BR>\r\nComments:<B>[% comments %]</B> | Votes:<B>[% voters %]</B>\r\n</FORM>\r\n',NULL,10000,'template','','        <TABLE WIDTH=\"[% width %]\" BORDER=\"0\" CELLPADDING=\"0\" CELLSPACING=\"0\"><TR VALIGN=\"TOP\">\r\n                <TD BGCOLOR=\"[% constants.bg.3 %]\"><IMG SRC=\"[% constants.imagedir %]/cl.gif\" WIDTH=\"7\" HEIGHT=\"10\" ALT=\"\"><IMG SRC=\"[% constants.imagedir %]/pix.gif\" WIDTH=\"4\" HEIGHT=\"4\" ALT=\"\"></TD>\r\n                <TD BGCOLOR=\"[% constants.bg.3 %]\" WIDTH=\"100%\">\r\n                        <TABLE WIDTH=\"100%\" BORDER=\"0\" CELLPADDING=\"2\" CELLSPACING=\"0\"><TR>\r\n                                <TD BGCOLOR=\"[% constants.bg.3 %]\"><FONT FACE=\"[% constants.mainfontface %]\" SIZE=\"[% constants.fontbase + 3 %]\" COLOR=\"[% constants.fg.1 %]\"><B>[% title %]</B></FONT></TD>\r\n                        </TR></TABLE>\r\n                </TD>\r\n                <TD BGCOLOR=\"[% constants.bg.3 %]\" ALIGN=\"right\"><IMG SRC=\"[% constants.imagedir %]/cr.gif\" WIDTH=\"7\" HEIGHT=\"10\" ALT=\"\"></TD>\r\n        </TR></TABLE>\r\n','',0,'',NULL,'','',0);
INSERT INTO blocks VALUES ('pollBooth-savepoll','[% FOREACH n = [1..8] %]\r\n    [% aid = \"aid$n\" %]\r\n    [% votes = \"votes$n\" %]\r\n    [% IF form.$aid %]\r\n<BR>Answer [% n %] \'[% form.$aid %]\' [% form.$votes %]\r\n    [% END %]\r\n[% END %]\r\n',NULL,10000,'template','','[% FOREACH n = [1..8] %]\r\n    [% aid = \"aid$n\" %]\r\n    [% votes = \"votes$n\" %]\r\n    [% IF form.$aid %]\r\n<BR>Answer [% n %] \'[% form.$aid %]\' [% form.$votes %]\r\n    [% END %]\r\n[% END %]\r\n','',0,'',NULL,'','',0);
INSERT INTO blocks VALUES ('pollBooth-listpolls','<!-- note that parameters passed to this template are passed to titlebar -->\r\n[% INCLUDE titlebar %]\r\n\r\n[% FOREACH question = questions %]\r\n   [% IF admin %]\r\n      [% edit = \" (<A HREF=\\\"$env.script_name?op=edit&qid=$question.0\\\">Edit</A>)\" %]\r\n   [% END %]\r\n<BR><LI><A HREF=\"[% env.script_name %]?qid=[% question.0 %]\">[% question.1 %]</A> [% question.2 %][% edit %]</LI>\r\n[% END %]\r\n\r\n<P><FONT SIZE=\"4\"><B><A HREF=\"[% env.script_name %]?min=[% startat %]\">More Polls</A></B></FONT>\r\n',NULL,10000,'template','','<!-- note that parameters passed to this template are passed to titlebar -->\r\n[% Slash.display(\'titlebar\') %]\r\n\r\n[% FOREACH question = questions %]\r\n   [% IF admin %]\r\n      [% edit = \" (<A HREF=\\\"$env.script_name?op=edit&qid=$question.0\\\">Edit</A>)\" %]\r\n   [% END %]\r\n<BR><LI><A HREF=\"[% env.script_name %]?qid=[% question.0 %]\">[% question.1 %]</A> [% question.2 %][% edit %]</LI>\r\n[% END %]\r\n\r\n<P><FONT SIZE=\"4\"><B><A HREF=\"[% env.script_name %]?min=[% startat %]\">More Polls</A></B></FONT>\r\n','',0,'',NULL,'','',0);
INSERT INTO blocks VALUES ('pollBooth-editpoll','<FORM ACTION=\"[% env.script_name %]\" METHOD=\"POST\">\r\n	<B>id</B> (if this matches a story\'s ID, it will appear with the story,\r\n		else just pick a unique string)<BR>\r\n	<INPUT TYPE=\"TEXT\" NAME=\"qid\" VALUE=\"[% qid %]\" SIZE=\"20\">\r\n	<INPUT TYPE=\"CHECKBOX\" NAME=\"currentqid\"[% checked %]> (appears on homepage)\r\n\r\n	<BR><B>The Question</B> (followed by the total number of voters so far)<BR>\r\n	<INPUT TYPE=\"TEXT\" NAME=\"question\" VALUE=\"[% question.question %]\" SIZE=\"40\">\r\n	<INPUT TYPE=\"TEXT\" NAME=\"voters\" VALUE=\"[% question.voters %]\" SIZE=\"5\">\r\n	<BR><B>The Answers</B> (voters)<BR>\r\n\r\n[% FOREACH n = [0 .. 7] %]\r\n	<INPUT TYPE=\"text\" NAME=\"aid[% n + 1%]\" VALUE=\"[% answers.$n.0 %]\" SIZE=\"40\">\r\n	<INPUT TYPE=\"text\" NAME=\"votes[% n + 1%]\" VALUE=\"[% answers.$n.1 or 0 %]\" SIZE=\"5\"><BR>\r\n[% END %]\r\n\r\n	<INPUT TYPE=\"SUBMIT\" VALUE=\"Save\">\r\n	<INPUT TYPE=\"HIDDEN\" NAME=\"op\" VALUE=\"save\">\r\n</FORM>\r\n',NULL,10000,'template','','<FORM ACTION=\"[% env.script_name %]\" METHOD=\"POST\">\r\n	<B>id</B> (if this matches a story\'s ID, it will appear with the story,\r\n		else just pick a unique string)<BR>\r\n	<INPUT TYPE=\"TEXT\" NAME=\"qid\" VALUE=\"[% qid %]\" SIZE=\"20\">\r\n	<INPUT TYPE=\"CHECKBOX\" NAME=\"currentqid\"[% checked %]> (appears on homepage)\r\n\r\n	<BR><B>The Question</B> (followed by the total number of voters so far)<BR>\r\n	<INPUT TYPE=\"TEXT\" NAME=\"question\" VALUE=\"[% question.question %]\" SIZE=\"40\">\r\n	<INPUT TYPE=\"TEXT\" NAME=\"voters\" VALUE=\"[% question.voters %]\" SIZE=\"5\">\r\n	<BR><B>The Answers</B> (voters)<BR>\r\n\r\n[% FOREACH n = [0 .. 7] %]\r\n	<INPUT TYPE=\"text\" NAME=\"aid[% n + 1%]\" VALUE=\"[% answers.$n.0 %]\" SIZE=\"40\">\r\n	<INPUT TYPE=\"text\" NAME=\"votes[% n + 1%]\" VALUE=\"[% answers.$n.1 or 0 %]\" SIZE=\"5\"><BR>\r\n[% END %]\r\n\r\n	<INPUT TYPE=\"SUBMIT\" VALUE=\"Save\">\r\n	<INPUT TYPE=\"HIDDEN\" NAME=\"op\" VALUE=\"save\">\r\n</FORM>\r\n','',0,'',NULL,'','',0);
INSERT INTO blocks VALUES ('pollBooth-data','[% SWITCH value %]\r\n\r\n[% CASE \'title\' %]\r\n    [% constants.sitename %] Poll\r\n\r\n[% CASE \'admin\' %]\r\n    <FONT SIZE=\"2\">[ <A HREF=\"[% env.script_name %]?op=edit\">New Poll</A> ]\r\n\r\n[% CASE \'invalid\' %]\r\n    Invalid poll!<BR>\r\n\r\n[% CASE \'display\' %]\r\n    Displaying poll results.\r\n\r\n[% CASE \'anon\' %]\r\n    You may not vote anonymously.  Please <A HREF=\"[% constants.rootdir %]/users.pl\">log in</A>.\r\n\r\n[% CASE \'uid_voted\' %]\r\n    [% user.nickname %] at [% env.remote_addr %] has already voted.\r\n    [% IF env.http_x_forwarded_for %]\r\n        (proxy for env.http_x_forwarded_for)\r\n    [% END %]\r\n\r\n[% CASE \'success\' %]\r\n    Your vote ([% aid %]) has been registered.\r\n\r\n[% CASE \'reject\' %]\r\n    Your vote ([% aid %]) was rejected.\r\n\r\n[% END %]\r\n',NULL,10000,'template','','[% SWITCH value %]\r\n\r\n[% CASE \'title\' %]\r\n    [% constants.sitename %] Poll\r\n\r\n[% CASE \'admin\' %]\r\n    <FONT SIZE=\"2\">[ <A HREF=\"[% env.script_name %]?op=edit\">New Poll</A> ]\r\n\r\n[% CASE \'invalid\' %]\r\n    Invalid poll!<BR>\r\n\r\n[% CASE \'display\' %]\r\n    Displaying poll results.\r\n\r\n[% CASE \'anon\' %]\r\n    You may not vote anonymously.  Please <A HREF=\"[% constants.rootdir %]/users.pl\">log in</A>.\r\n\r\n[% CASE \'uid_voted\' %]\r\n    [% user.nickname %] at [% env.remote_addr %] has already voted.\r\n    [% IF env.http_x_forwarded_for %]\r\n        (proxy for env.http_x_forwarded_for)\r\n    [% END %]\r\n\r\n[% CASE \'success\' %]\r\n    Your vote ([% aid %]) has been registered.\r\n\r\n[% CASE \'reject\' %]\r\n    Your vote ([% aid %]) was rejected.\r\n\r\n[% END %]\r\n','',0,'',NULL,'','',0);
