INSERT INTO blocks VALUES ('pollBooth-vote','<CENTER><TABLE BORDER=\"0\" CELLPADDING=\"2\" CELLSPACING=\"0\" WIDTH=\"500\">\r\n	<TR><TD> </TD><TD COLSPAN=\"1\">\r\n\r\n[% INCLUDE titlebar %]\r\n\r\n<FONT SIZE=\"2\">[% notes %]</FONT></TD></TR>\r\n\r\n[% FOR poll = pollitems %]\r\n\r\n    [% answer     = poll.0\r\n       imagewidth = poll.1\r\n       votes      = poll.2\r\n       percent    = poll.3\r\n    %]\r\n\r\n	<TR>\r\n		<TD WIDTH=\"100\" ALIGN=\"RIGHT\">[% answer %] &nbsp;</TD>\r\n		<TD WIDTH=\"450\"><NOBR><IMG\r\n			SRC=\"[% constants.imagedir %]/leftbar.gif\" WIDTH=\"4\" HEIGHT=\"20\" ALT=\"\"><IMG\r\n			SRC=\"[% constants.imagedir %]/mainbar.gif\" HEIGHT=20\r\n			width=\"[% imagewidth %]\" ALT=\"[% percent %]%\"><IMG\r\n			SRC=\"[% constants.imagedir %]/rightbar.gif\" WIDTH=\"4\" HEIGHT=\"20\" ALT=\"\">\r\n			[% votes %] /\r\n			<FONT COLOR=\"#006666\">[% percent %]%</FONT></NOBR>\r\n		</TD>\r\n	</TR>\r\n\r\n[% END %]\r\n\r\n	<TR><TD COLSPAN=\"2\" ALIGN=\"RIGHT\">\r\n		<FONT SIZE=\"4\"><B>[% voters %] total votes.</B></FONT>\r\n	</TD></TR><TR><TD COLSPAN=\"2\"><P ALIGN=\"CENTER\">\r\n		[\r\n			<A HREF=\"[% env.script_name %]?qid=[% qid %]\">Voting Booth</A> |\r\n			<A HREF=\"[% env.script_name %]\">Other Polls</A> |\r\n			<A HREF=\"[% constants.rootdir %]/\">Back Home</A>\r\n		]\r\n	</TD></TR><TR><TD COLSPAN=\"2\">[% postvote %]</TD></TR>\r\n</TABLE></CENTER>\r\n\r\n',NULL,10000,'template','','<CENTER><TABLE BORDER=\"0\" CELLPADDING=\"2\" CELLSPACING=\"0\" WIDTH=\"500\">\r\n	<TR><TD> </TD><TD COLSPAN=\"1\">\r\n\r\n[% Slash.display(\'titlebar\') %]\r\n\r\n<FONT SIZE=\"2\">[% notes %]</FONT></TD></TR>\r\n\r\n[% FOR poll = pollitems %]\r\n\r\n    [% answer     = poll.0\r\n       imagewidth = poll.1\r\n       votes      = poll.2\r\n       percent    = poll.3\r\n    %]\r\n\r\n	<TR>\r\n		<TD WIDTH=\"100\" ALIGN=\"RIGHT\">[% answer %] &nbsp;</TD>\r\n		<TD WIDTH=\"450\"><NOBR><IMG\r\n			SRC=\"[% constants.imagedir %]/leftbar.gif\" WIDTH=\"4\" HEIGHT=\"20\" ALT=\"\"