INSERT INTO blocks VALUES ('pollbooth','<FORM ACTION=\"[% env.script_name %]\">\r\n	<INPUT TYPE=\"hidden\" NAME=\"qid\" VALUE=\"[% qid %]\">\r\n    [% IF constants.currentSection %]\r\n	<INPUT TYPE=\"hidden\" NAME=\"section\" VALUE=\"[% constants.currentSection %]\">\r\n    [% END %]\r\n<B>[% question %]</B>\r\n\r\n\r\n[% FOREACH poll = polls %]\r\n    [% answer   = poll.1\r\n       aid      = poll.2\r\n    %]\r\n	<BR><INPUT TYPE=\"radio\" NAME=\"aid\" VALUE=\"[% aid %]\">[% answer %]\r\n[% END %]\r\n\r\n<BR><INPUT TYPE=\"submit\" VALUE=\"Vote\">\r\n[ <A HREF=\"[% env.script_name %]?[% sect %]qid=[% qid %]&aid=[% constants.anonymous_coward_uid %]\"><B>Results</B></A> |\r\n<A HREF=\"[% env.script_name %]?[% sect %]\"><B>Polls</B></A> ] <BR>\r\nComments:<B>[% comments %]</B> | Votes:<B>[% voters %]</B>\r\n</FORM>\r\n',NULL,10000,'template','','        <TABLE WIDTH=\"[% width %]\" BORDER=\"0\" CELLPADDING=\"0\" CELLSPACING=\"0\"><TR VALIGN=\"TOP\">\r\n                <TD BGCOLOR=\"[% constants.bg.3 %]\"><IMG SRC=\"[% constants.imagedir %]/cl.gif\" WIDTH=\"7\" HEIGHT=\"10\" ALT=\"\"><IMG SRC=\"[% constants.imagedir %]/pix.gif\" WIDTH=\"4\" HEIGHT=\"4\" ALT=\"\"></TD>\r\n                <TD BGCOLOR=\"[% constants.bg.3 %]\" WIDTH=\"100%\">\r\n                        <TABLE WIDTH=\"100%\" BORDER=\"0\" CELLPADDING=\"2\" CELLSPACING=\"0\"><TR>\r\n                                <TD BGCOLOR=\"[% constants.bg.3 %]\"><FONT FACE=\"[% constants.mainfontface %]\" SIZE=\"[% constants.fontbase + 3 %]\" COLOR=\"[% constants.fg.1 %]\"><B>[% title %]</B></FONT></TD>\r\n                        </TR></TABLE>\r\n                </TD>\r\n                <TD BGCOLOR=\"[% constants.bg.3 %]\" ALIGN=\"right\"><IMG SRC=\"[% constants.imagedir %]/cr.gif\" WIDTH=\"7\" HEIGHT=\"10\" ALT=\"\"></TD>\r\n        </TR></TABLE>\r\n','',0,'',NULL,'','',0);
